(function (window, document, $, undefined) {
    $(function () {
        $('#cms-widget-ab44e712-4694-495f-8cbf-ea3d5f6c36bf').on('click', '.meta-event-attachment-link', function (e) {
            var $target = $(e.currentTarget);
            var p = $.ajax({
                url: $target.attr('data-ajax'),
            });
            p.done(function (resp) {
                $(resp)
                    .appendTo($('body'))
                    .modal({
                        show: false
                    })
                    .on('show.bs.modal', function () {
                        $(this).css('display', 'block');
                        var $d = $(this).find('.modal-dialog'),
                            o = ($(window).height() - $d.height()) / 2,
                            bm = parseInt($d.css('marginBottom'), 10);
                        o = Math.max(o, bm);
                        $d.css('margin-top', o);
                    })
                    .on('hidden.bs.modal', function () {
                        $(this).remove();
                    })
                    .modal('show');
            });
            e.preventDefault();
        });
    });
})(window, document, jQuery);

function googleTranslateElementInit() {
    new google.translate.TranslateElement({
        pageLanguage: 'en',
                    layout: google.translate.TranslateElement.InlineLayout.SIMPLE,
                        gaTrack: true,
            gaId: 'UA-136068333-1'
                }, 'google_translate_element');

        }
