$("#mkSearchForm select").on('change', function(){

		var blank = true;
		if( $("#mkSearchForm input[name='mkSrch']").val() != "" || $("#mkSearchForm input[name='mkSrch']").val().trim().length != 0 ){ blank = false;  }
		if( $("select[name='crop[]'] option:selected").length > 0){ blank = false; }
		if( $("select[name='livestock[]'] option:selected").length > 0){ blank = false; }
		if( $("select[name='state[]'] option:selected").length > 0){ blank = false; }
		if( $("select[name='media[]'] option:selected").length > 0){ blank = false; }
		if( $("select[name='resource[]'] option:selected").length > 0){ blank = false; }

		if(blank){ 
			alert('Please type a keyword or select a field to begin search'); return false; 
		}else{
			$("#mkResultWrap").html('

').show(0);
			$('.mkThankyou').remove();
			$.ajax({
				url: 'https://peyton.k12.co.us/wp-content/themes/peyton/mkQuery.php', 
				type : "POST",
				data : $("#mkSearchForm").serialize(),
				success : function(result) {
					//console.log(result);
					$("#mkResultWrap").html(result).show(0);
					$('ul.alphaBtns li').removeClass('active');
					$('#mkResultWrap .Alphaset').each(function(){
						var letter = $(this).data('letter');
						$('ul.alphaBtns li[data-letter='+letter+']').addClass('active');
					});

					$('.col-md-12.alphabets-namelist ul').matchHeight({ byRow: true, property: 'height', target: null, remove: false });
				},
				error: function(xhr, resp, text) {
					$("#mkResultWrap").html('


Sorry there were an error! Please try again.').show(0);
				}
			});
		}
	});




