$( ".tablinks" ).on( "click", function() {
var tabTop = $(".tab").offset().top;
var tabH = $(".tab").innerHeight();
  $('html, body').animate({
        scrollTop: (tabTop+tabH)+'px'
    }, 1000);
});
}     


function activeMenu() {
    var curr_url = window.location.pathname ;
         var curr_menu4 = $(".menu a[href$='" + curr_url + "']");
    $(curr_menu4).parent(".parentSubMenu").css("background", "#231f20");
    var curr_menu1 = $(".online-academy-menu a[href$='" + curr_url + "']");
    $(curr_menu1).parent("li").css("background", "#231f20");

    var curr_menu2 = $(".col-md-12.online-academy-menu.junsen a[href$='" + curr_url + "']");
    $(curr_menu2).parent("li").css("background", "#d92722");

     var curr_menu3 = $(".staff a[href$='" + curr_url + "']");
    $(curr_menu3).parent("li").css("background", "#231f20");




}
activeMenu();


