$('ul.alphaBtns li').on('click', function(){
if($(this).hasClass('active')){
	var letter = $(this).data('letter');
	$('#mkResultWrap .Alphaset:not([data-letter='+letter+'])').hide();
	$('#mkResultWrap .Alphaset[data-letter='+letter+']').show();
}
});

$('#mkResultWrap .Alphaset').each(function(){
	var letter = $(this).data('letter');
	$('ul.alphaBtns li[data-letter='+letter+']').addClass('active');
});


$(window).load(function(){
    var elupevURL = window.location.hash;
    if(elupevURL){
		var elemL = $(elupevURL).length;
		if(elemL > 0){
			var elt = $(elupevURL).offset().top;
			var sth = $('header .stickyTop').height();
			setTimeout(function(){ $('html, body').animate({'scrollTop' : (elt - (sth+10))+'px'}); },200);
		}
    }
});

$('#mega-menu-wrap-header-menu li.mega-menu-item a[href*="#"]').on('click', function(){
    var elupevURL = window.location.hash;
    if(elupevURL){
		var elemL = $(elupevURL).length;
		if(elemL > 0){
			var elt = $(elupevURL).offset().top;
			var sth = $('header .stickyTop').height();
			setTimeout(function(){ $('html, body').animate({'scrollTop' : (elt - (sth+10))+'px'}); },500);
		}
    }
});

//OVERRIDES VISUAL COMPOSER TAB FUNCTIONALITY (JUST BECAUSE OF STICKY MENU)
$('body.wpb-js-composer .vc_tta-tab>a').on('click', function(e){
    e.preventDefault();
    e.stopPropagation();
    $(this).parent().addClass('vc_active').siblings().removeClass('vc_active');
    var wt = $(window).scrollTop();
    var ste = $(this).attr('href');
    var stp = $(this).parents('.vc_general').offset().top;
    var ht = $('.stickyTop').innerHeight();
    var kp = 10;
    //console.log(ste);

    $(ste).addClass('vc_active').siblings().removeClass('vc_active');
    if( (wt+ht+kp) > stp){
        $('html, body').animate({'scrollTop' : (stp - (ht+kp))+'px'});
    }

});


});
