link.script:
$(function () {
	if( $( '#emergencyAlert' ).length ) {
		$.magnificPopup.open({
			items: {
				src: $( '#emergencyAlert' ), // can be a HTML string, jQuery object, or CSS selector
				type: 'inline'
			}
		});
$(document).on('click', '.popup-modal-dismiss', function (e) {
	e.preventDefault();
	$.magnificPopup.close();
});
	}
});

$(document).ready(function() {
  var owl = $('#newsItem');
  owl.owlCarousel({
      navigation : true, // Show next and prev buttons
      slideSpeed : 300,
      paginationSpeed : 400,
      singleItem:true,
      autoHeight:true
  });

// for dots
 $('.owl-carousel .owl-controls .owl-page').attr('tabindex', 0).attr('role', "button");
 $('#newsItem .owl-page').on("keydown", function(e){
   var code = e.keyCode || e.which;
   if (code === 13) { // enter key
     n = $(this).index();
     owl.trigger('owl.goTo', n);
   }
 });

  // for next and prev buttons
 $('.owl-carousel .owl-controls .owl-next').attr('tabindex', 0).attr('role', "button");
 $('.owl-carousel .owl-controls .owl-prev').attr('tabindex', 0).attr('role', "button");
 $('#newsItem .owl-prev, #newsItem .owl-next').on("keydown", function(e){
   var code = e.keyCode || e.which;
   if (code === 13) { // enter key
     n = $(this).index();
     owl.trigger('owl.goTo', n);
   }
 });
 
});
