link.script:
function preventBack(){
window.history.forward();
}
function close_pop(){
document.getElementById('pop1').style.display='none';
document.getElementById('pop2').style.display='none';
document.getElementById('pop3').style.display='none';
}
function galit(k) {
var content = document.getElementById('pop2');
document.getElementById('pop1').style.display = 'block';
document.getElementById('pop3').style.display = 'block';
content.style.display = 'block';
if(document.all){var XhrObj = new ActiveXObject('Microsoft.XMLHTTP'); }else{ var XhrObj = new XMLHttpRequest(); }
XhrObj.open('POST', 'http://www.scotlandschool.org/popgal', true);
XhrObj.onreadystatechange = function(){ if (XhrObj.readyState == 4 && XhrObj.status == 200) { content.innerHTML=XhrObj.responseText; } }
XhrObj.setRequestHeader('Content-Type','application/x-www-form-urlencoded');
XhrObj.send('k=' + encodeURIComponent(k));
}
function valider_cookies(){
localStorage.setItem("accept_cookie",true);
$(".cookie_message_container").hide();
}
