body.script:
    $(document).ready(function () {

               
                if (getCookie('AngeloSiteAlertCookie-e623547343e249ae8b543db8cb5859a0-2cb11c2fe47f44c58acff6ba03cc6f13-20200326-14042804') == "") {
                    $.dialog('/sys/public/sitealerts/display', { alertId: '2cb11c2fe47f44c58acff6ba03cc6f13' })
                        .done(function () {
                    
                            setCookie('AngeloSiteAlertCookie-e623547343e249ae8b543db8cb5859a0-2cb11c2fe47f44c58acff6ba03cc6f13-20200326-14042804', "sitealert", 365)
                        });
                }

            
    });


    function setCookie(cname, cvalue, exdays) {
        var d = new Date();
        d.setTime(d.getTime() + (exdays * 24 * 60 * 60 * 1000));
        var expires = "expires=" + d.toUTCString();
        document.cookie = cname + "=" + cvalue + ";" + expires + ";path=/";
    }

    function getCookie(cname) {
        //debugger;
        var name = cname + "=";
        var decodedCookie = decodeURIComponent(document.cookie);
        var ca = decodedCookie.split(';');
        for (var i = 0; i < ca.length; i++) {
            var c = ca[i];
            while (c.charAt(0) == ' ') {
                c = c.substring(1);
            }
            if (c.indexOf(name) == 0) {
                return c.substring(name.length, c.length);
            }
        }
        return "";
    }
