	$(window).scroll(function(e){
		if($(window).width() >= 1024) { 
			stickyHeader();
		}
	});

	function stickyHeader() {
		var scrolled = $(window).scrollTop();
		if(scrolled == 0) {
			if(!$("#hp-alert-outer .ui-article").length || $(".sp").length) {
				$("#gb-header-outer, #gb-header").height("145px");
				$("#gb-channel-list-outer").css({"position":"relative" , "margin-top":"191px"});
				$("#hp-slideshow-outer, #sp-content-outer, #spn-content-outer").css("margin-top" , "0");
				$(".fade-logo").hide();
			} else {
				$("#hp-alert-outer, #hp-alert").height(initHeight).css("z-index","4");
				$("#gb-mystart-outer").css("margin-top" , alertHeight);
				$("#gb-channel-list-outer").css("margin-top" , channelMargin+"px");
				$("#gb-header-outer").css({"margin-top":headerMargin+"px" , "height":"145px"});
				$("#gb-header").css("height" , "145px");
				$(".fade-logo").fadeOut();
			}
		} else if(scrolled > 0) {
			if(!$("#hp-alert-outer .ui-article").length || $(".sp").length) {
				var newHeight = $("#gb-header-outer, #gb-header").height()-scrolled;
				//$("#gb-header-outer, #gb-header").height(newHeight);
				//$("html, body").scrollTop($("#gb-header-outer, #gb-header").scrollTop() + 190);
				if(scrolled >= 145) {
					$("#gb-channel-list-outer").css({"position":"fixed" , "width":"100%" , "z-index":"7000" , "margin-top":"45px"});
					$("#hp-slideshow-outer, #sp-content-outer, #spn-content-outer").css("margin-top" , "233px");
					$(".fade-logo").fadeIn();
				} else {
					$("#gb-channel-list-outer").css({"position":"relative" , "margin-top":"191px"});
					$("#hp-slideshow-outer, #sp-content-outer, #spn-content-outer").css("margin-top" , "0");
					$(".fade-logo").fadeOut();
				}
			} else {
				var newHeight = $("#gb-header-outer, #gb-header").height()-scrolled;
				$("#gb-header-outer, #gb-header").height(newHeight);
				var newAlertHeight = $("#hp-alert-outer, #hp-alert").height()-scrolled;
				$("#gb-header-outer, #gb-header").height(newAlertHeight);

				if(scrolled > alertHeight) {
					$("#gb-channel-list-outer").css({"position":"fixed" , "width":"100%" , "z-index":"7000" , "margin-top":alertHeight+45+"px"});
					$("#hp-slideshow-outer, #sp-content-outer, #spn-content-outer").css("margin-top" , "233px");
					$(".fade-logo").fadeIn();
				} else {
					$("#gb-channel-list-outer").css({"position":"relative" , "margin-top":"191px"});
					$("#hp-slideshow-outer, #sp-content-outer, #spn-content-outer").css("margin-top" , "0");
					$(".fade-logo").fadeOut();
				}
			}
		}
	}

	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
			$(".column.full div.ui-widget.app.upcomingevents ul.ui-articles li").last().addClass("last");

		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
			appView();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
			appView();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			photoGallery();
			appView480();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			photoGallery();
			appView480();
		}
	}

