	function appView(){
		//DOC make homepage apps collapsible for small mobile devices
		if(!$("#content-top div.ui-widget.app.active").length){
			$("#content-top div.ui-widget.app:first").addClass("active");
		}
		$("#content-top div.ui-hp div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
		});
	}

	function appView480(){
		//DOC make homepage apps collapsible for small mobile devices
		if(!$("#content-top div.ui-widget.app.active, #content-bottom div.ui-widget.app.active").length){
			$("#content-top div.ui-widget.app:first").addClass("active");
		}
		$("#content-top div.ui-hp div.ui-widget-header, #content-bottom div.ui-hp div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
		});
	}	

