");
		}
        }

	function dynamicContent(){
		// ********** MYSTART ********** //
		//ADD FRAMESET TRANSLATOR TO #MYSTART-LEFT
		addFramesetTranslate();
		//MOVE #SW-MYSTART-SEARCH TO #MYSTART-LEFT
		$("#sw-mystart-search").appendTo("#mystart-left");
		//DOC replace mystart dropdown arrows
		$(".sw-myaccount-chevron").addClass("icon-font").text("1");
		$(".sw-dropdown-arrow").addClass("icon-font").text("3");
		$("#sw-search-button span").addClass("icon-font").text("S");

		// ********** ALERT ********** //
		$("#hp-alert .ui-widget.app").addClass("ui-clear");
		if($("#hp-alert .ui-widget.app .ui-article").length) {
			$("#hp-alert-outer").show();	
		}

		// ********** HEADER ********** //

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

		//CHANNEL SLIDER
		var element; 
		var leftPos;
		var newWidth;
		var mainNav = $("#channel-navigation");

		$("
").appendTo(mainNav);
		var sliderLine = $("#channel-slider");

		if($("li.sw-channel-item.active").size()) {
			sliderLine.width(0);
			//sliderLine.width($("li.sw-channel-item").eq(0).width() + 20).css("left", $("li.sw-channel-item").eq(0).position().left - 10).data("origLeft", sliderLine.position().left).data("origWidth", sliderLine.width());
		} else {

			sliderLine.width($("li.sw-channel-item").eq(0).width() + 20).css("left", $("li.sw-channel-item").eq(0).position().left - 10).data("origLeft", sliderLine.position().left).data("origWidth", sliderLine.width());	
		}

		$("#sw-channel-list-container").hover(function() {
			sliderLine.show();
		}, function() {
			sliderLine.hide();
		});

		$("li.sw-channel-item").hover(function() {
			leftPos = $(this).position().left + 20;
			newWidth = $(this).width() - 40;
			sliderLine.stop().animate({ left: leftPos, width: newWidth });
		}, function() {
			sliderLine.stop().animate({ left: sliderLine.data("origLeft"), width: sliderLine.data("origWidth") });    
		});


		//FULL WIDTH CHANNELS
		// RESET THE MARGIN
	        $("li.sw-channel-item").css("margin", "");

	        // SET UP OUR VARIABLES
	        var channelNum = $("li.sw-channel-item").length, channelWidths = 0, channelBarWidth = $("#sw-channel-list-container").width() - 1, availSpace = 0, channelPad = 0;

	        // GET THE TOTAL WIDTH OF ALL THE CHANNELS
	        $("li.sw-channel-item").each(function(){
	                channelWidths += $(this).width();
	        });

	        // GET THE TOTAL AMOUNT OF EMPTY SPACE LEFT OVER
	        availSpace = channelBarWidth - channelWidths;

	        // GET THE AMOUNT OF SPACE TO SET BETWEEN EACH CHANNEL
	        // SUBTRACT ONE FROM THE TOTAL CHANNEL NUMBER BECAUSE WE WANT THE LAST CHANNEL TO SIT AGAINST THE RIGHT EDGE OF THE TEMPLATE
	        channelPad = Math.floor(availSpace / (channelNum - 1));

	        // NOW APPLY THE MARGIN
	        $("li.sw-channel-item").not($("li.sw-channel-item.last")).css("padding-right", channelPad + "px");


		// ********** BODY ********** //
		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").text("Home");
		//DOC replace more-link text
		$("a.more-link span").text("More »");
		//DOC replace ui-return text for headlines
		$(".headlines.detail .ui-return span").text("« Return to Headlines");
		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();
		//DOC center signed out message and signin button
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        		//DOC add signed out breadcrumb
        		$(".ui-breadcrumbs").append("
Signed Out");
        	}
        	//DOC add sign in breadcrumb
        	if($("#swlogin").length){
        		$(".ui-breadcrumbs").append("
Sign In");
        	}
        	//MOD EVENTS
        	$(".upcomingevents").modEvents({
			columns:"yes",
			monthLong:"no",
			dayWeek:"no"
		});
		eventsByDay(".upcomingevents .ui-articles");
		if($("div.ui-widget.app.upcomingevents .ui-article-description").text() == "There are no upcoming events to display.") {
			$("div.ui-widget.app.upcomingevents .ui-article-description").css("padding","0px");
		}
		$(".column.full .ui-widget.app.upcomingevents .ui-articles").addClass("ui-clear");

		if(!$(".hp.column.two .ui-widget.app").length) {
			$(".hp.column.two").hide();
			$(".hp.column.one").css("width","100%");
		}


		// ********** FOOTER ********** //
		//DOC get Schoolwires footer links
		var feedbackURL = $("div#sw-footer-links li:eq(0) a").attr("href");
		var termsURL = $("div#sw-footer-links li:eq(2) a").attr("href");
		var policyURL = $("div#sw-footer-links li:eq(1) a").attr("href");

		$("a#feedback").attr("href", feedbackURL);
		$("a#terms").attr("href", termsURL);
		$("a#policy").attr("href", policyURL);	

		//DOC mystart user options
		$("
USER
").prependTo("#footer-mystart");

		var signinBtn = $("#sw-mystart-right .sw-mystart-button.signin").html();
		$("
" + signinBtn + "").prependTo("#user-options");
		$("#sw-mystart-right .sw-mystart-button.signin").hide();
		if($(".sw-mystart-button.register").length) {
			var registerBtn = $("#sw-mystart-right .sw-mystart-button.register").html();
			$("
" + registerBtn + "").appendTo("#user-options");
			$("#sw-mystart-right .sw-mystart-button.register").hide();
		}

		$(".sw-mystart-button.options").click(function() {
			$(this).addClass("clicked");
			$(this).hover(function() {

			},function() {
				$(this).removeClass("clicked");
			});
		});		
	}

	function eventsByDay(container) {
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();
			};
		});
	}

	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
			$("div#sw-mystart-search input#sw-search-input").attr("value", "SEARCH THIS SITE");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "SEARCH THIS SITE") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("SEARCH THIS SITE");
			}
		});
	}

