    function headlinesScroller() {
    	if($(".hp").length) {
            $("#sw-content-container11.region.ui-hp div.ui-widget.app").appScroller({
                    "appParent"             : "#slideshow-content-left",
                    "targetApp"             : "headlines", //upcomingevents or headlines
                    "imageNum960"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 960
                    "imageNum768"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 768
                    "imageNum640"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 640
                    "imageNum480"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 480
                    "imageNum320"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 320
                    "backIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
                    "nextIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
                    "buttonColor"           : "#FFF", //COLOR TO BE USED FOR THE BACK AND NEXT BUTTONS
                    "extraBackIconClass"    : "",
                    "extraNextIconClass"    : "",
                    "controlGutter"         : "0" //LEFT and RIGHT MARGINS TO ALLOW ROOM FOR CONTROLS
            });

            if(!$("#slideshow-content-left .ui-widget.app.headlines .scroller-control-cont").length) {
            	$("#slideshow-content-left .ui-widget.app.headlines .ui-widget-detail").after($("
"));
            }

            $("#slideshow-content-left div.ui-widget.app.headlines .headlines-scroll-ctrl.next").prependTo(".scroller-control-cont");
            $('').prependTo(".scroller-control-cont");
            $("#slideshow-content-left div.ui-widget.app.headlines .headlines-scroll-ctrl.back").prependTo(".scroller-control-cont");

            if($("#slideshow-content-left .ui-widget.app.headlines a.more-link").length) {
            	$("#slideshow-content-left .ui-widget.app.headlines a.more-link span").text("read more");
            	if(!$("#slideshow-content-left .ui-widget.app.headlines .scroller-control-cont a.more-link").length) {
                	$("#slideshow-content-left .ui-widget.app.headlines a.more-link").appendTo(".scroller-control-cont");
                }
            }

            var scrollerFocus = false;

            //PAUSE PLAY HEADLINES
            $(".headlines-play-pause-btn").click(function() {
                $(this).toggleClass("pause");
                $(this).toggleClass("play");
                //pausePlayHeadlines();
                if($(".cs-video-btn").hasClass("pause")) {
                    $(".cs-video-btn").attr("aria-label","Pause Video");
                    scrollerFocus = false;
                } else {
                    $(".cs-video-btn").attr("aria-label","Play Video");
                    scrollerFocus = true;
                }
            });

            setInterval(function() {

              $("#slideshow-content-left .ui-widget.app.headlines .ui-article-title a, #slideshow-content-left .ui-widget.app.headlines .headlines-scroll-ctrl").focus(function() {
                  scrollerFocus = true;
              });

              $("#slideshow-content-left .ui-widget.app.headlines .ui-article-title a, #slideshow-content-left .ui-widget.app.headlines .headlines-scroll-ctrl").focusout(function() {
                  scrollerFocus = false;
              });

              if(scrollerFocus != true) { 
                      $("#slideshow-content-left .ui-widget.app.headlines .headlines-scroll-ctrl.next").click();
              }
            }, 8000);
        }
    }

    function pausePlayHeadlines() {
    	if($(".cs-video-btn").hasClass("pause")) {
            $(".cs-video-btn").attr("aria-label","Pause Video");
            //player.playVideo();
        } else {
            $(".cs-video-btn").attr("aria-label","Play Video");
            //player.pauseVideo();
        }
    }


	function jsMediaQueries(){
		switch(csGetBreakPoint()) {
			case "desktop": 
				//$(".hp.column.one .upcomingevents li:gt(6)").remove();
                $(".hp.column.one .upcomingevents li").slice(5).remove();
                $(window).scroll(parallaxScroll);
			break;
			case "768":   

                //$(".hp.column.one .upcomingevents li:gt(3)").remove();
                $(".hp.column.one .upcomingevents li").slice(3).remove();
                $(window).scroll(parallaxScroll);
			break;
			case "640":

                 //$(".hp.column.one .upcomingevents li:gt(3)").remove();
                 $(".hp.column.one .upcomingevents li").slice(3).remove();
                 $(window).scroll(parallaxScroll);
                 var attr = $("ul.sw-channel-list li.sw-channel-item").attr('aria-haspopup');
                 $("ul.sw-channel-list li.sw-channel-item").on("click",function(e){
                 	if((!$(".ios").length) && $(this).hasClass("has-dd")){
                		e.preventDefault();
                    } 
                 	$("ul.sw-channel-dropdown",this).css("left","0px");
                 });
                 $(".ios ul.sw-channel-list li.sw-channel-item").on("touchstart",function(e){
                 	$("ul.sw-channel-dropdown",this).css("left","0px");
                 });
			break;
			case "480":

                //$(".hp.column.one .upcomingevents li:gt(3)").remove();
                $(".hp.column.one .upcomingevents li").slice(3).remove();
				appView();
                var attr = $("ul.sw-channel-list li.sw-channel-item").attr('aria-haspopup');
                 $("ul.sw-channel-list li.sw-channel-item").on("click",function(e){
                 	if((!$(".ios").length) && $(this).hasClass("has-dd")){
                		e.preventDefault();
                    } 
                 	$("ul.sw-channel-dropdown",this).css("left","0px");
                 });
                 $(".ios ul.sw-channel-list li.sw-channel-item").on("touchstart",function(e){
                 	$("ul.sw-channel-dropdown",this).css("left","0px");
                 });
			break;
			case "320":

                //$(".hp.column.one .upcomingevents li:gt(3)").remove();
                $(".hp.column.one .upcomingevents li").slice(3).remove();
				appView();
                var attr = $("ul.sw-channel-list li.sw-channel-item").attr('aria-haspopup');
                 $("ul.sw-channel-list li.sw-channel-item").on("click",function(e){
                 	if((!$(".ios").length) && $(this).hasClass("has-dd")){
                		e.preventDefault();
                    } 
                 	$("ul.sw-channel-dropdown",this).css("left","0px");
                 });
                 $(".ios ul.sw-channel-list li.sw-channel-item").on("touchstart",function(e){
                 	$("ul.sw-channel-dropdown",this).css("left","0px");
                 });
			break;
		}
	}

	function mediaRotator() {
    	var mediaChoice = "Video";
            if(mediaChoice == "Video"){
            	//$("#hp-slideshow #sw-content-container10").addClass("hidden");
                $("#hp-slideshow #sw-content-container10").remove();
                $("#hp-player").removeClass("hidden");
            } else if(mediaChoice == "Rotator"){
                if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
                        // MULTIMEDIA ROTATOR
                        $("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
                                "imageWidth"              : 1500,
                                "imageHeight"             : 915,
                                "efficientLoad"           : "yes",
                                "appHeader"               : "no",
                                "titles"                  : "yes",
                                "captions"                : "yes",
                                "galleryOverlay"          : "no",
                                "linkedElement"           : ["image"], // ["image", "title", "overlay", "all"]
                                "playPause"               : "no",
                                "mainControls"            : "no",
                                "contMoreLink"            : "no",
                                "contMoreLinkText"        : "Read More",
                                "descMoreLink"            : "yes",
                                "descMoreLinkText"        : "read more",
                                "descVideoLink"           : "yes",
                                "descVideoLinkText"       : "Watch Video",
                                "bullets"                 : "yes",
                                "bulletControls"          : "no",
                                "thumbnails"              : "no",
                                "thumbnailWidth"          : 122,
                                "thumbnailHeight"         : 53,
                                "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
                                "hoverPause"              : "yes",
                                "autoRotate"              : "yes",
                                "fullScreenRotator"       : "yes", // image dimensions work well at 1500x915
                                "fullScreenBreakpoints"   : ["960"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
                                "fullScreenVideo"         : "no",
                                "transitionType"          : "fade", // fade or slide
                                "transitionSpeed"         : 3,
                                "transitionDelay"         : 8,
                                "onImageLoad"             : function() { },
                                "allImagesLoaded"         : function() { },
                                "onThumbnailLoad"         : function() { },
                                "allThumbnailsLoaded"     : function() { },
                                "onTransitionStart"       : function() { },
                                "onTransitionEnd"         : function() { },
                                "watchVidClick"           : function() { },
                                "closeVidClick"           : function() { },
                                "allLoaded"               : function() { 
                                                        $("#hp-slideshow .rs-gallery-container .rs-gallery-desc .rs-gallery-desc-more-link a span").html('read  more');
                                                        var getWidth = $(window).width();
                                                        if (getWidth < 1024) {
                                                            var imageHeight = $("#hp-slideshow .rs-gallery-container .rs-gallery-images").height() - 12;
                                                            if(!$("#hp-slideshow.media-option-Video").length) {
                                                                $("#hp-slideshow #hp-scroll-down").css("top",imageHeight);
                                                            }    
                                                        }  
                                                        $(window).resize(function() {
                                                            var getWidth = $(window).width();
                                                            if (getWidth < 1024) {
                                                                var imageHeight = $("#hp-slideshow .rs-gallery-container .rs-gallery-images").height() - 12;
                                                                if(!$("#hp-slideshow.media-option-Video").length) {
                                                                    $("#hp-slideshow #hp-scroll-down").css("top",imageHeight);
                                                                }    
                                                            }  
                                                        });
                                }

                        });
                        $("#hp-player").remove();
                }
          }      
	}

