    function checkSlideshow() {
        if($(".hp").length && csGetBreakPoint() != "desktop") {
              if ($(window).scrollTop() <= $("#hp-slideshow").offset().top + $("#hp-slideshow").height()) {
                  if(csSlideshowDescFixed) {
                      $("#hp-slideshow .rs-gallery-desc").removeAttr("style");

                      csSlideshowDescFixed = false;
                  }
              } else {
                  if(!csSlideshowDescFixed) {
                      $("#hp-slideshow .rs-gallery-desc").css({
                                      "height": $("#hp-slideshow .rs-gallery-desc").height(),
                                      "overflow": "hidden"
                      });

                      csSlideshowDescFixed = true;
                  }
              }
        }
    }

	function appView(){
		//MAKE HOMEPAGE APPS COLLAPSIBLE FOR SMALL MOBILE DEVICES
		if(!$("main div.ui-widget.app.active").length){
			$("main div.ui-widget.app:first").addClass("active");
		}

		//DONT ADD APP INTO ACCORDIAN IF IT DOESNT HAVE A HEADER
		$("main div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});

		//ACCORDIAN HEADER CLICK
		$("main div.ui-widget-header").off("click").click(function(){

            $(this).next("div.ui-widget-detail").slideToggle("slow");
            $(this).parent().toggleClass("active");
		});
	}	

	function useChannelForSection(){
		//USE CHANNEL NAME FOR PAGE LIST NAVIGATION HEADER IF ONE NOT PRESENT
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
    }   

	function dynamicContent(){

		// ********** MYSTART ********** //

		//TO FIX THE ISSUE OF SEARCH NOT ALLOWING SPACES
        $("#sw-mystart-search").unbind();

		//APPEND SEARCH TO GB-SEARCH-BOTTOM
		$("#sw-mystart-search").appendTo(".gb-footer-mystart.center");

        //ADD ROLE AND ARIA LABEL TO SEARCH BUTTON
        $("#sw-search-button").attr("role","button").attr("aria-label","Submit Search").addClass("icon-font-search");

        //REPLACE MYSTART DROPDOWN ARROWS
		$(".sw-myaccount-chevron").text("");
		$(".sw-dropdown-arrow").text("").addClass("icon-font-wave3");

        //ADD TRANSITION CLASS TO ALL MYSTART DROPDOWNS
		$(".sw-mystart-dropdown").addClass("transition");


        $(".sw-mystart-dropdown.schoollist").prependTo(".gb-footer-mystart.left");

        //CHANGE THE SCHOOLLIST TEXT
		$(".sw-mystart-dropdown.schoollist .selector").html('OurSchools');

		//ADD TRANSITION CLASS TO ALL MYSTART BUTTONS
		$(".sw-mystart-button").addClass("transition");

		//APPEND MYSTART ITEMS TO APPROPRIATE PLACES

        $(".sw-mystart-button.signin").appendTo("#gb-user-options");
        $(".sw-mystart-button.register").appendTo("#gb-user-options");


        //DOC ADD CUSTOM CLICK EVENT FOR SCHOOLLIST DROPDOWN
		$(".sw-mystart-dropdown").off();
		$(".sw-mystart-dropdown").click(function(){
			$(this).toggleClass("open");
			$(".sw-dropdown-arrow",this).toggleClass("open");
			$(".sw-dropdown", this).slideToggle("slow", "swing");
		});

		//DOC ADD CUSTOM CLICK EVENT FOR MYACCOUNT DROPDOWN
		$("#sw-mystart-account .sw-dropdown").attr("id","cs-sw-myaccount-list").removeClass("sw-dropdown");
		$("#sw-mystart-account").off();
		$("#sw-mystart-account").click(function(){
			$(".icon-font-arrow",this).toggleClass("open");
			$("#cs-sw-myaccount-list").slideToggle("slow", "swing");
		});

        //ADD DISTRICT HOME TO SCHOOLLIST
        var districtHome = '
District Home'
        $(".gb-footer-mystart.left  .sw-mystart-dropdown.schoollist ul.sw-dropdown-list").prepend(districtHome);

		// ********** HEADER ********** //

		//ADD LOGO
		var logoCheck = "true";
		var logoSrc = jQuery.trim("/cms/lib/IL02217852/Centricity/Template/GlobalAssets/images///Logo/North Shore District Logo.png");
		if(logoCheck == "true") {
			var srcSplit = logoSrc.split("/");
			var srcSplitLen = srcSplit.length;
			if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
				$("#gb-logo").append('').removeClass("hidden");
			} else {
				$("#gb-logo").append('').removeClass("hidden");
			}
		}

        //ADD SITENAME
        var siteNameOne = jQuery.trim('');
        var siteNameTwo = jQuery.trim('');
        if((siteNameOne == "") && (siteNameTwo == "")) {
            var splitLen = 2;
            var siteName = "North Shore School District 112";
            siteName = siteName.split(" ");
            var siteNameLength = siteName.length;
            if(siteNameLength > 2){
                siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
            } else {
                siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
            }
            siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
            $("#gb-sitename").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
        } else if((siteNameOne != "") && (siteNameTwo == "")) {
            $("#gb-sitename").prepend("
" + siteNameOne + "");
        } else if((siteNameOne == "") && (siteNameTwo != "")) {
            $("#gb-sitename").prepend("
" + siteNameTwo + "");
        } else if((siteNameOne != "") && (siteNameTwo != "")) {
            $("#gb-sitename").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
        }

        var windowWidth = $(window).width();
        var contentWidth = $(".gb-footer-mystart-inner").width();
        var controlOffset = (windowWidth - contentWidth)/2;
        $("#play-pause-outer").css("right",controlOffset);

        //HIDE SITENAME IF LOGO IS BEING USED
        if($('#gb-logo.show-logo-true').length) {
        	$('#gb-sitename').hide();
            $('#gb-tagline').hide();
        } else {
        	$('#gb-tagline').removeClass("hidden");
        }

		// ********** CHANNEL BAR ********** //

		//ADD FIRST CLASS TO LAST CHANNEL ITEM
		$("li.sw-channel-item").first().addClass("first");
		//ADD LAST CLASS TO LAST CHANNEL ITEM
		$("li.sw-channel-item").last().addClass("last");

		//ADD TRANSITION CLASS TO ALL CHANNEL ITEMS
		$("ul.sw-channel-list li.sw-channel-item").addClass("transition");

        $("ul.sw-channel-list li.sw-channel-item").each(function(){
        	if($(".sw-channel-dropdown",this).length){
            	$(this).addClass("has-dd");
            }
        });


        //ADD CLASS TO PASSKEY
        $("#gb-user-options a#ui-btn-mypasskey span").addClass("icon-font-passkeys").text("");


    	var windowHeight = $(window).height();
        var channelNumber = $("ul.sw-channel-list > li").length;
        var channelHeight = (windowHeight - 46 - channelNumber)/(channelNumber + 1);
        $("#gb-channel-list-inner").css("height",windowHeight);
        $("#gb-channel-list-inner h2").css("height",channelHeight);
        $("#gb-channel-list-inner ul.sw-channel-list li.sw-channel-item").css("height",channelHeight);
        if(windowWidth >= 768){
        	$("#gb-channel-list-inner ul.sw-channel-list ul.sw-channel-dropdown").css("top",-channelHeight);
        } else {
        	$("#gb-channel-list-inner ul.sw-channel-list ul.sw-channel-dropdown").css("top","0px");
        }
        $(window).resize(function() {
        	var windowHeight = $(window).height();
            var channelNumber = $("ul.sw-channel-list > li").length;
            var channelHeight = (windowHeight - 46 - channelNumber)/(channelNumber + 1);
            $("#gb-channel-list-inner").css("height",windowHeight);
            $("#gb-channel-list-inner h2").css("height",channelHeight);
            $("#gb-channel-list-inner ul.sw-channel-list li.sw-channel-item").css("height",channelHeight);
            if(windowWidth >= 768){
                $("#gb-channel-list-inner ul.sw-channel-list ul.sw-channel-dropdown").css("top",-channelHeight);
            } else {
                $("#gb-channel-list-inner ul.sw-channel-list ul.sw-channel-dropdown").css("top","0px");
            }
        });    

        $("ul.sw-channel-list li.sw-channel-item").each(function(){
        	var channelName = $("> a > span",this).text();
            $(".sw-channel-dropdown",this).prepend('
' + channelName + '');

        });

		// ********** BODY ********** //

		//RENAME FIRST BREADCRUMB
		$("li.ui-breadcrumb-first > a > span").text("Home");

		//REPLACE MORE-LINK TEXT
		$("a.more-link span").text("More »");

		//AUTO-FOCUS UERSNAME SIGIN FIELD
		$("#swsignin-txt-username").focus();

		//CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        		//ADD SIGNED OUT BREADCRUMB
        		$(".ui-breadcrumbs").append("
Signed Out");
        	}

        //ADD READ FULL STORY BUTTON TO EACH HEADLINE
        $(".sp .ui-widget.app.headlines").each(function(){
            $(".ui-article", this).each(function(){
                var headlineURL = $("h1 a", this).attr("href");
                $('read more').insertAfter($(".ui-article-description", this));
            });
        });

        //ADD SIGN IN BREADCRUMB
        if($("#swlogin").length){
            $("#sw-content-layout-wrapper .ui-widget.app").addClass("signin");
            $(".ui-breadcrumbs").append("
Sign In");
        }

        	//REGISTER BREADCRUMB
		if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
			$("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
			$(".ui-breadcrumbs").append("
Register");
		}

		//SEARCH RESULTS BREADCRUMB
		if($("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").length) {
			$("#sw-content-layout-wrapper").children().wrapAll('

');
			$(".ui-breadcrumbs").append("
Search Results");
		}

		//CHANGE TEXT OF CALENDAR LINK
		$("a.view-calendar-link").text("view full calendar");

		//LIMIT EVENTS ON HOMEPAGE - REMOVE IF NOT NEEDED
		//$(".hp.column.one .upcomingevents li:gt(6)").remove();
        //$(".hp.column.one .upcomingevents li").slice(5).remove();

		//SCROLL FUNCTION FOR BACK TO TOP BUTTON
		$("#scroll-up").click(function(e){
        		e.preventDefault();
        		$("html, body").animate({ scrollTop: $("body").offset().top}, "slow");
                $("#gb-channel-list .menu").focus();
        });

        $("#scroll-up").keydown(function(e){
        	if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
            	e.preventDefault();
        		$("html, body").animate({ scrollTop: $("body").offset().top}, "slow");
                $("#gb-channel-list .menu").focus();
            }
		});

        //SLIDESHOW ARROW SCROLL
		$("#hp-slideshow #hp-scroll-down").click(function(){
			$("html, body").animate({scrollTop: ($("#hp-content-one-outer").offset().top)}, "slow");	
		});

        $("#hp-slideshow #hp-scroll-down").keydown(function(e){
        	if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
            	e.preventDefault();	
                $("html, body").animate({scrollTop: ($("#hp-content-one-outer").offset().top)}, "slow");
                $("#hp-content-one a:first").focus();
            }    
		});

		// APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
        $("div.ui-widget.app .ui-widget-detail img")
            .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
            .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
            .each(function() {
                if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                    $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                }
        });

        //ADD WE ARE LOGO IN CONTENT-THREE
		var weAreCheck = "false";
		var weAreLogoSrc = jQuery.trim("/cms/lib/IL02217852/Centricity/Template/GlobalAssets/images///background/Layer 2.png");
        var weAreText = jQuery.trim("");
		if(weAreCheck == "true") {
			var weAreSrcSplit = weAreLogoSrc.split("/");
			var weAreSrcSplitLen = weAreSrcSplit.length;
			if((weAreLogoSrc != "") && (weAreSrcSplit[weAreSrcSplitLen - 1] != "default-man.jpg")) {
				$(".hp.column.six").append('
').removeClass("hidden");
			} else {
				$(".hp.column.six").append('
').removeClass("hidden");
			}
            //APPEND THE WE ARE TEXT
            $(".hp.column.six").append('
' + weAreText + '').removeClass("hidden");
		}

        //MOVE THE READ MORE TO THE HEADER
		//$(".ui-widget.app.upcomingevents .ui-widget-detail .view-calendar-link").appendTo(".ui-widget.app.upcomingevents .ui-widget-header");

        $(".ui-widget.app.upcomingevents").each(function(appIndex, app){

                  if( $('.view-calendar-link', app).length > 0 ){

                      $('.view-calendar-link', app).appendTo( $('.ui-widget-header', app) );
                  } 
            });

        $(".hp.column.seven div.region .app:first").addClass("first-app");

        //CHANGE TEXT FOR RSS 
        $("a.ui-btn-toolbar.rss span").text("subscribe");

        //IF ONLY ONE APP IN REGION E&F WILL EXPAND TO 100% WIDTH
        var regEapps = $(".hp.column.four .ui-widget.app").length;
        var regFapps = $(".hp.column.five .ui-widget.app").length;
        if(regEapps == "1"){
        	$(".hp.column.four").addClass("one-app");
        }
        if(regFapps == "1"){
        	$(".hp.column.five").addClass("one-app");
        }


		// ********** FOOTER ********** //

        $(".gb.footer.three > .gb-sitemap > a").addClass("icon-font-sitemap");

        //STICKEY FOOTER SIDE WIDTHS
        var windowWidth = $(window).width();
        var contentWidth = $(".gb-footer-mystart-inner").width();
        var offset = (windowWidth - contentWidth)/2 - 1;
        $(".gb-footer-sticky-outer").css("width",offset);
        $(window).resize(function() {
        	var windowWidth = $(window).width();
            var contentWidth = $(".gb-footer-mystart-inner").width();
            var offset = (windowWidth - contentWidth)/2 - 1;
            $(".gb-footer-sticky-outer").css("width",offset);
        });

        //DOC ADD CLICK EVENT FOR POPULAR LINKS
		$("#gb-footer-sticky-outer .global-trigger").off();
		$("#gb-footer-sticky-outer .global-trigger").click(function(){
			$(this).toggleClass("open");
            $("#gb-global-icons").toggleClass("open");
			$("#gb-global-icons").slideToggle("slow", "swing").attr("aria-hidden", ($("#gb-global-icons").attr("aria-hidden") == "true") ? "false" : "true");
		});

        $("#gb-global-outer").hover(function(){

			},function(){
			$(".global-trigger", this).removeClass("open");
			$("#gb-global-icons", this).removeClass("open");
			$("#gb-global-icons", this).slideUp("slow", "swing").attr("aria-hidden","false");
		});

        //DOC ADD CLICK EVENT FOR SOCIAL ICONS AT 640
		$("#gb-social-trigger").off();
		$("#gb-social-trigger").click(function(){
			$(this).toggleClass("open");
            $("#gb-social-icons").toggleClass("open");
			$("#gb-social-icons").slideToggle("slow", "swing");
		});

		//PHONE AND FAX NUMBERS
		if("224-765-3000" != "") {
			$(".gb-footer-contact.phone").removeClass("hidden");
		}
		if("" != "") {
			$(".gb-footer-contact.fax").removeClass("hidden");
		}
		//GET SCHOOLWIRES FOOTER LINKS
		var feedbackURL = $("div#sw-footer-links li:eq(0) a").attr("href");
		var termsURL = $("div#sw-footer-links li:eq(2) a").attr("href");
		var policyURL = $("div#sw-footer-links li:eq(1) a").attr("href");
		var swLogo = $("#sw-footer-logo a img").attr("src");
		var swURL = $("#sw-footer-logo a").attr("href");

		$("a#feedback").attr("href", feedbackURL);
		$("a#terms").attr("href", termsURL);
		$("a#policy").attr("href", policyURL);	
		$("#sw-logo a").attr("href", swURL);	
        $("#sw-logo a img").attr("src", swLogo);		
	}

    function channelBar() {
    	var windowHeight = $(window).height();

