        });
    }

	function searchText() {
		//CUSTOM MYSTART SEARCH PLACEHOLDER
	    //SEARCH TEXT
        switch(csGetBreakPoint()) {
				case "desktop":
                    $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
                    $("div#sw-mystart-search input#sw-search-input").attr("value", "Search North Shore School District");
                    $("div#sw-mystart-search input#sw-search-input").focus(function() {
                        if($(this).val() == "Search North Shore School District") {
                            $(this).val("");
                        }
                    });
                    $("div#sw-mystart-search input#sw-search-input").blur(function() {
                        if($(this).val() == "") {
                            $(this).val("Search North Shore School District");
                        }
                    });
                break; 
				case "768": case "640": case "480": case "320":        
                    $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
                    $("div#sw-mystart-search input#sw-search-input").attr("value", "Search");
                    $("div#sw-mystart-search input#sw-search-input").focus(function() {
                        if($(this).val() == "Search") {
                            $(this).val("");
                        }
                    });
                    $("div#sw-mystart-search input#sw-search-input").blur(function() {
                        if($(this).val() == "") {
                            $(this).val("Search");
                        }
                    });
				break;
          }      

	}

