	function modEvents(){
		if (!$(".upcomingevents .joel-month").length) {
            $(".upcomingevents").modEvents({
                columns     : "yes",
                monthLong   : "yes",
                dayWeek     : "yes"
            });
            eventsByDay(".upcomingevents .ui-articles");
        }
	}		

	function eventsByDay(container) {
		//MOVE ALL EVENTS WITH THE SAME DATES INTO THE SAME CONTAINER
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();

			};
		});

		//ADD CLASSES TO EACH UPCOMING EVENT LI IF LIMITING THEM - REMOVE IF NOT NECESSARY
		if($(".hp").length) {

			// ADD CLASSES TO ARTICLES
			//$("#hp-region-three .upcomingevents .ui-articles").wrap("
");
			$(".hp.column.one .upcomingevents li").first().addClass("first");
			$(".hp.column.one .upcomingevents li").last().addClass("last");
			var eventClass = ["one", "two", "three", "four", "five"];
			$(".hp.column.one .upcomingevents li").each(function() {
				var thisIndex = $(this).index();
				$(this).addClass("event-" + eventClass[thisIndex]);
			});
			$('.hp.column.one .ui-widget.app.upcomingevents .ui-widget-detail .ui-articles').each(function(){
				$(this).addClass("ui-clear");
			});
		}
	}

	function calendarListView(){
		//AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}

    var parallaxScroll = function(){
		var getWidth = $(window).width();
		var getHeight = $(window).height();
		var headerHeight = $("header").height();
		if (getWidth >= 480) { // 960+
			if($(window).scrollTop() > headerHeight) {
				$("#scroll-up").fadeIn(); 

			} else {
				$("#scroll-up").fadeOut();  
			}

		}
	}

    function translateControls() {
    	$("#gb-translate-inner li a").attr("tabindex","-1");
    	$("#gb-translate button").click(function(e){
            e.preventDefault();
            animateTranslate();
        });
        $("#gb-translate button").keydown(function(e){
            if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
            	e.preventDefault();
            	animateTranslate();
            }
        });

        $(".gb-translate-list li a").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS LAST ITEM
                        $(this).closest(".gb-translate-list").find("li:last-child > a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).closest(".gb-translate-list").find("li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest(".gb-translate-list").find("li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest(".gb-translate-list").find("li:last-child > a").focus();
                break;
            }
        });


        var translateTimer = 0;
        $("#gb-translate-inner").mouseenter(function() {
        	clearTimeout(translateTimer);
        });
        $("#gb-translate-inner").mouseleave(function() {
        	translateTimer = setTimeout(function(){
            	$("#gb-translate-inner").css("left","-259px");
                $("#gb-translate button").attr("aria-label", "Open Translate Menu").removeClass("open");
                $("#gb-translate-inner").removeClass("open").attr("aria-hidden", "true");
                $("#gb-translate button").focus();
                $("#gb-translate-inner li a").attr("tabindex","-1");
            }, 1000);
        });

        var translateFocusTimer = 0;
        $("#gb-translate-inner").focusin(function() {
        	clearTimeout(translateFocusTimer);
        });
        $("#gb-translate-inner").focusout(function() {
        	translateFocusTimer = setTimeout(function(){
            	$("#gb-translate-inner").css("left","-259px");
                $("#gb-translate button").attr("aria-label", "Open Translate Menu").removeClass("open");
                $("#gb-translate-inner").removeClass("open").attr("aria-hidden", "true");
                $("#gb-translate button").focus();
                $("#gb-translate-inner li a").attr("tabindex","-1");
            }, 1000);
        });
    }    

	function animateTranslate() {
    	if(!$("#gb-translate-inner").hasClass("open")) {
            $("#gb-translate button").attr("aria-label", "Close Translate menu").addClass("open");
            $("#gb-translate-inner").addClass("open");

            $("#gb-translate-inner").css("left","0px").attr("aria-hidden", "false");
            $("#gb-translate-inner li a").first().focus();
            $("#gb-translate-inner li a").attr("tabindex","0");
        } else {
        	$("#gb-translate-inner").css("left","-259px");
            $("#gb-translate button").attr("aria-label", "Open Translate menu").removeClass("open");
            $("#gb-translate-inner").removeClass("open").attr("aria-hidden", "true");
            $("#gb-translate button").focus();
            $("#gb-translate-inner li a").attr("tabindex","-1");
        }
    }

