    }

    function animateChannelBar() {
    	if(!$("#gb-channel-list-inner").hasClass("open")) {
        	$("#gb-channel-list button").attr("aria-label", "Close Main menu").addClass("open");
            $("#gb-channel-list-inner").addClass("open");
            $("#gb-channel-list-inner").css("left","0px").attr("aria-hidden", "false");
            $("#gb-channel-list-inner ul.sw-channel-list li.sw-channel-item > a").first().focus();
            $("#gb-channel-list-inner ul.sw-channel-list li.sw-channel-item > a").attr("tabindex","0");
            $("#gb-channel-list-inner ul.sw-channel-list li.sw-channel-item .sw-channel-dropdown").hide();
            $("#ui-btn-mypasskey").attr("tabindex", "0");
        	$("#ui-btn-sitemanager").attr("tabindex", "0");
        	$("#sw-myaccount").attr("tabindex", "0");
            $("#ui-btn-signin a").attr("tabindex", "0");
        	$("#ui-btn-register a").attr("tabindex", "0");
        } else {
        	/*$("#gb-channel-list-inner").css("left","-317px");
            $("#gb-channel-list button").attr("aria-label", "Open Main menu").removeClass("open");
            $("#gb-channel-list-inner").removeClass("open").attr("aria-hidden", "true");
            $("#gb-channel-list button").focus();
            $("#gb-channel-list-inner ul.sw-channel-list li.sw-channel-item > a").attr("tabindex","-1");
            $("#gb-channel-list-inner ul.sw-channel-list li.sw-channel-item .sw-channel-dropdown").hide();
            $("#ui-btn-mypasskey").attr("tabindex", "-1");
        	$("#ui-btn-sitemanager").attr("tabindex", "-1");
        	$("#sw-myaccount").attr("tabindex", "-1");
            $("#ui-btn-signin a").attr("tabindex", "-1");
       		$("#ui-btn-register a").attr("tabindex", "-1");*/
        }

        var channelTimer = 0;
        $("#gb-channel-list-inner").focusin(function() {
        	clearTimeout(channelTimer);
        });

        $("#gb-channel-list-inner").focusout(function() {
        	channelTimer = setTimeout(function(){
        		$("#gb-channel-list-inner").css("left","-317px");
                $("#gb-channel-list button").attr("aria-label", "Open Main menu").removeClass("open");
            	$("#gb-channel-list-inner").removeClass("open").attr("aria-hidden", "true");
            	$("#gb-channel-list button").focus();
            	$("#gb-channel-list-inner ul.sw-channel-list li.sw-channel-item > a").attr("tabindex","-1");
                $("#gb-channel-list-inner ul.sw-channel-list li.sw-channel-item .sw-channel-dropdown").hide();
                $("#ui-btn-mypasskey").attr("tabindex", "-1");
                $("#ui-btn-sitemanager").attr("tabindex", "-1");
                $("#sw-myaccount").attr("tabindex", "-1");
                $("#ui-btn-signin a").attr("tabindex", "-1");
                $("#ui-btn-register a").attr("tabindex", "-1");
            }, 1000);    
        });

        var translateTimer = 0;
        $("#gb-channel-list-inner").mouseenter(function() {
        	clearTimeout(channelTimer);
        });
        $("#gb-channel-list-inner").mouseleave(function() {
        	channelTimer = setTimeout(function(){
            	$("#gb-channel-list-inner").css("left","-317px");
                $("#gb-channel-list button").attr("aria-label", "Open Main menu").removeClass("open");
            	$("#gb-channel-list-inner").removeClass("open").attr("aria-hidden", "true");
            	$("#gb-channel-list button").focus();
            	$("#gb-channel-list-inner ul.sw-channel-list li.sw-channel-item > a").attr("tabindex","-1");
                $("#gb-channel-list-inner ul.sw-channel-list li.sw-channel-item .sw-channel-dropdown").hide();
                $("#ui-btn-mypasskey").attr("tabindex", "-1");
                $("#ui-btn-sitemanager").attr("tabindex", "-1");
                $("#sw-myaccount").attr("tabindex", "-1");
                $("#ui-btn-signin a").attr("tabindex", "-1");
                $("#ui-btn-register a").attr("tabindex", "-1");
            }, 1000);
        });

    }

	function csGetBreakPoint() {
		return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
	}

    function accessibility() {

    	$(".sw-channel-item").each(function(){
            $("> a", this).attr("tabindex", "-1"); 
        });

        $("#ui-btn-mypasskey").attr("tabindex", "-1");
        $("#ui-btn-sitemanager").attr("tabindex", "-1");
        $("#sw-myaccount").attr("tabindex", "-1");
        $("#ui-btn-signin a").attr("tabindex", "-1");
        $("#ui-btn-register a").attr("tabindex", "-1");

        $("#gb-footer-sticky-outer .global-trigger").keydown(function(e){
        	if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
            	e.preventDefault();
        		$(this).toggleClass("open");
                $("#gb-global-icons").toggleClass("open");
                $("#gb-global-icons").slideToggle("slow", "swing").attr("aria-hidden", ($("#gb-global-icons").attr("aria-hidden") == "true") ? "false" : "true");
                if(!$(this).hasClass("open")) {
                	$(this).focus();
                } else {
                	$("#gb-global-icons .global-icon:first").focus();
                }

            }
		});

        $("#gb-footer-sticky-outer #gb-global-icons a").keydown(function(e){
        	// CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).is(":first-child")) {
                        // FOCUS LAST ITEM
                        $("#gb-global-icons a:last-child").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).prev("a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if($(this).is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $("#gb-global-icons a:first-child").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).next("a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $("#gb-global-icons a:first-child").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $("#gb-global-icons a:last-child").focus();
                break;
            }
		});

    	$("#sw-search-button").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.space:
                	e.preventDefault();
                    SWGoToSearchResultsPageswsearchinput();

                break;
            }
        });

        $(".sw-mystart-dropdown").each(function(i) {
            // SET ATTRIBUTES
            $(".selector", this).attr("id", "mystart-dropdown-" + (i + 1));
            $(".sw-dropdown-list", this).attr("role", "menu").attr("aria-labelledby", $(".selector", this).attr("id"));
            $(".sw-dropdown", this).attr("aria-hidden", "true");
            $(".sw-dropdown-list li", this).attr("role", "menuitem");
            $(".sw-dropdown-list li a", this).attr("tabindex", "0");
        });

        $(".sw-mystart-dropdown").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
            	e.preventDefault();
        		$(this).toggleClass("open");
				$(".sw-dropdown-arrow",this).toggleClass("open");
				$(".sw-dropdown", this).slideToggle("slow", "swing").attr("aria-hidden", ($(".sw-dropdown").attr("aria-hidden") == "true") ? "false" : "true");
                if(!$(this).hasClass("open")) {
                	$(this).focus();
                } else {
                	$(".sw-dropdown-list li:first-child a", this).focus();
                }

        	}
        });

        $(".sw-mystart-dropdown").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.down:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(".sw-dropdown-list li:first-child a", this).focus();
                break;
            }
        });

        $(".sw-mystart-dropdown .sw-dropdown-list li a").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS LAST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                break;
            }
        });

        $("#cs-sw-myaccount-list li").each(function(){
            $("> a", this).attr("tabindex", "0"); 
        });

        $("#sw-myaccount").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.down:
                case csKeyCodes.enter:
                case csKeyCodes.space:
                    e.preventDefault();


                    $(this).next("#cs-sw-myaccount-list").slideToggle("slow", "swing").attr("aria-hidden", ($("#cs-sw-myaccount-list").attr("aria-hidden") == "true") ? "false" : "true");


                    // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                 	if(!$(this).hasClass("open")) {
                          // TOGGLE THE DROPDOWN
                     	$(this).addClass("open");
                      	$(this).next("#cs-sw-myaccount-list").find("li:first-child a").focus();
                    } else {
                      	$(this).removeClass("open");
                    }
                    // FOCUS FIRST ITEM

                break;
            }
        });  

        $("#cs-sw-myaccount-list li a").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS LAST ITEM
                        $(this).closest("#cs-sw-myaccount-list").find("li:last-child > a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).closest("#cs-sw-myaccount-list").find("li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }
                break;

                // CONSUME HOME KEY
                case csKeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest("#cs-sw-myaccount-list").find("li:first-child > a").focus();
                break;

                // CONSUME END KEY
                case csKeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest("#cs-sw-myaccount-list").find("li:last-child > a").focus();
                break;

            }
        });

    }

