    function csOpenPageSubMenu(li) {
        if (li.prop('tagName').toLowerCase() == "li") {
            if (li.hasClass('expandable')) {  
                li.removeClass('expandable').addClass('collapsible');
            }
            if (li.find('div:first').hasClass('expandable')) {
                li.find('div:first').removeClass('expandable').addClass('collapsible');
            }
            li.find('ul:first').slideDown();

            PageNavigationStateCookie();
        }
    }

    function csClosePageSubMenu(li) {
        if (li.prop('tagName').toLowerCase() == "li") {
            li.removeClass('collapsible').addClass('expandable');
            li.find('div:first').removeClass('collapsible').addClass('expandable');	 
            li.find('ul:first').slideUp();

            PageNavigationStateCookie();
        }
    }

	var mediaChoice = "Video";
	if(mediaChoice == "Video"){
          //loads the IFrame Player API code asynchronously.
          var tag1 = document.createElement('script');
          tag1.src = "https://www.youtube.com/iframe_api";
          var firstScriptTag1 = document.getElementsByTagName('script')[0];
          firstScriptTag1.parentNode.insertBefore(tag1, firstScriptTag1);

          //creates an  (and YouTube player)
          //after the API code downloads.
          var player;
          var windowWidth;
          var windowHeight;
          function onYouTubeIframeAPIReady() {
                  windowWidth = $(window).width();
                  windowHeight = $(window).height();
                  player = new YT.Player('player', {
                      height: windowHeight,
                      width: windowWidth,
                      videoId: 'J2Mjqbh12eA',
                      playerVars: { 
                          'modestbranding': 0,
                              'controls': 0, 
                              'rel' : 0,
                              'loop': 1,
                              'showinfo': 0,
                              'wmode': 'opaque'
                      },
                      events: {
                          'onReady': onPlayerReady,
                          'onStateChange': onPlayerStateChange
                      }
                  });
          }

          //the API will call this function when the video player is ready.
          function onPlayerReady(event) {
              if(!$("html").hasClass("mobile")) {
                  event.target.playVideo();
              } else {
                  $(".play-pause").removeClass("play").attr("aria-label","Play Video").addClass("pause");
              }

              csVideoResize();

              $(".play-pause").click(function() {
              		$(this).toggleClass("pause");
                  	$(this).toggleClass("play");
                  	pausePlayVideo();
              });
              $("#gb-mystart-outer").addClass("test");
          }

          //The API calls this function when the players state changes.
          //The function indicates that when playing a video (state=1),
          //the player should play for six seconds and then stop.
          var done = false;
          function onPlayerStateChange(event) {
              if(!$("html").hasClass("mobile")) {
                 //player.playVideo();
                 pausePlayVideo();
              }
          }
          function pausePlayVideo() {
              if($(".play-pause").hasClass("pause")) {
              		$(".play-pause").attr("aria-label","Pause Video");
                  	player.playVideo();
                  	player.mute();

              } else {
                  	$(".play-pause").attr("aria-label","Play Video");
                  	player.pauseVideo();
              }
          }
          function stopVideo() {
              player.stopVideo();
          }



    }      

	function csVideoResize() {
		if($("#player").length) {
			switch(csGetBreakPoint()) {
				case "desktop":
					var topOffSet = $("#hp-player").offset().top;
					var screenWidth = $(window).width();
					var screenHeight = $(window).height() - topOffSet;
					var screenRatio = screenHeight / screenWidth;
                        		var containerHeight = screenWidth * screenRatio;
					var vidWidth = screenWidth, vidHeight = 0;

					// GET IMAGE RATIO
					var initW = 853.5, initH = 480;
					var ratio = initH / initW;
					vidHeight = vidWidth * ratio;

					if(vidHeight < screenHeight){
						vidHeight = screenHeight;
						vidWidth = vidHeight / ratio;
					}

		                        var centerWidth = (vidWidth - screenWidth) / 2;
		                        var centerHeight = (vidHeight - screenHeight) / 2;

					// APPLY THE DIMENSIONS
					$("#hp-player").css({"height": containerHeight + "px"});
					$("#player").css({"width": vidWidth + "px", "height": vidHeight + "px", "margin": "-" + centerHeight + "px 0px 0px -" + centerWidth + "px"});
				break;
				case "768": case "640": case "480": case "320":
					var screenWidth = $(window).width();
					var screenHeight = $(window).height();
					var vidWidth = screenWidth, vidHeight = 0;

					// GET IMAGE RATIO
					var initW = 853.5, initH = 480;
					var ratio = initH / initW;
					vidHeight = vidWidth * ratio;

					// APPLY THE DIMENSIONS
					$("#hp-player").css({"height": "auto"});
					$("#player").css({"width": "100%", "height": vidHeight + "px", "margin": "0px"});
				break;
			}
		}

	}

(function ($) {
    $.fn.tabBlocks = function () {
		return this.each(function() {
			var element = this;

			// SET PANEL ATTRIBUTES
			$("div[id*='pmi']", element).not($("div[id*='pmi'] div[id*='pmi']", element)).each(function(i) {
				var thisPMI = $(this).attr("id").replace("pmi-", "");
				$(this).addClass("tab-panel").attr("aria-labelledby", "cs-tab-" + thisPMI + "-" + (i + 1)).attr("aria-hidden", "true").attr("role", "tabpanel").attr("data-index", i);
			});

			// CREATE TAB CONTAINER
			$(element).prepend("

");

			// CREATE TABS AND SET ATTRIBUTES
			$("div.ui-widget.app", element).not($("div.ui-widget.app div.ui-widget.app", element)).each(function(i) {
				var myHeader = $("div.ui-widget-header h1", this).html();
				$(this).parents("div.region.ui-hp").find("div.tabs-container div.clear").before("
" + myHeader + "");
				if($(this).hasClass("blog")) {
						$(this).parent().wrap('
'); 
				}
			});

			// INITIALIZATION
			//$("div.tabs-container .tab-button:eq(0)", element).addClass("active").attr("aria-selected", "true").attr("tabindex", "0");
			//$(".tab-panel", element).eq(0).attr("aria-hidden", "false");

            var tabsContHeight = $("#slideshow-conent-tabs div.tabs-container").height() * 2;
            $("#slideshow-conent-tabs div.tabs-container").css({
            	"top": "calc(100% - " + tabsContHeight + "px)"
            });
            var ie11tabsContHeight = tabsContHeight + 149;
            $(".ie11 #slideshow-conent-tabs div.tabs-container").css({
            	"top": "calc(100% - " + ie11tabsContHeight + "px)"
            });
			$("div.ui-widget.app", element).addClass("activeTabApp");
			$("div.ui-widget.app div.ui-widget.app", element).removeClass("activeTabApp");
			//$("div.ui-widget.app:eq(0)", element).addClass("active-app");



			// TAB CLICK
			$("div.tab-button", element).click(function () {
            	$("#slideshow-conent-tabs").css("right","0");
                if($(this).hasClass("active")) {
                	$(this).removeClass("active");
                    $("div.ui-widget.app", element).removeClass("active-app");
                    $("#slideshow-conent-tabs").css("right","-253px");
                    $(".ie11 #slideshow-conent-tabs").css("right","-143px");
                } else {
					switchTabs($(".tab-button.active", element), $(this));
                }
			});

			// TAB KEYDOWN
			$(".tab-button", element).keydown(function(e) { 
            	$("#slideshow-conent-tabs").css("right","0");
				handleTabEvent($(this), e);
			});

			// TAB MOUSE OVER
			$(".tab-button", element).live("mouseover mouseout", function (event) {
				if (event.type == "mouseover") {
					$(this).addClass("hover");
				} else {
					$(this).removeClass("hover");
				}
			});

			// PANEL KEYDOWN
			$(".tab-panel", element).keydown(function(e) { 
				handlePanelEvent($(this), e);
			});

			function handleTabEvent(tab, e) {
				var lastIndex = $(".tab-button", element).length - 1;

				// ONLY CONSUME IF NOT USING ALT KEY
				if (!e.altKey) {
					switch(e.keyCode) {
						// MOVE ONE STEP BACKWARDS
						case 37 : // LEFT ARROW
						case 38 : // UP ARROW
							if (!e.ctrlKey) { // CTRL+ARROW MOVES FOCUS FROM PANEL TO OPEN TAB - MAKE SURE THAT IS NOT BEING PRESSED TOO
								e.preventDefault();
								e.stopPropagation();

								// GET THE PREVIOUS TAB
								var prevTab;
								if (parseInt(tab.attr("data-index")) == 0) { 
									prevTab = $(".tab-button[data-index='" + lastIndex + "']", element); // LAST TAB
								} else { 
									prevTab = $(".tab-button[data-index='" + (parseInt(tab.attr("data-index")) - 1) + "']", element); // PREVIOUS TAB
								} 

								// SWITCH THE TABS
								switchTabs(tab, prevTab);
							}
						break;

						// MOVE ONE STEP FORWARDS
						case 39 : // RIGHT ARROW
						case 40 : // DOWN ARROW
							if (!e.ctrlKey) { // CTRL+ARROW MOVES FOCUS FROM PANEL TO OPEN TAB - MAKE SURE THAT IS NOT BEING PRESSED TOO
								e.preventDefault();
								e.stopPropagation();

								// GET THE NEXT TAB
								var nextTab;
								if (parseInt(tab.attr("data-index")) == lastIndex) { 
									nextTab = $(".tab-button[data-index='0']", element); // FIRST TAB
								} else { 
									nextTab = $(".tab-button[data-index='" + (parseInt(tab.attr("data-index")) + 1) + "']", element); // PREVIOUS TAB
								} 

								// SWITCH THE TABS
								switchTabs(tab, nextTab);
							}
						break;

						// JUMP TO FIRST TAB
						case 36 : // HOME KEY
							e.preventDefault();
							e.stopPropagation();

							switchTabs(tab, $(".tab-button[data-index='0']", element));
						break;

						// JUMP TO LAST TAB
						case 35 : // END KEY
							e.preventDefault();
							e.stopPropagation();

							switchTabs(tab, $(".tab-button[data-index='" + lastIndex + "']", element));
						break;
					}
				}
			}

			function handlePanelEvent(tab, e) {
				var lastIndex = $(".tab-button", element).length - 1;

				// ONLY CONSUME IF NOT USING ALT KEY
				if (!e.altKey) {
					switch(e.keyCode) {
						case 27 : // ESC KEY
							e.preventDefault();
							e.stopPropagation();
						break;

						case 37 : // LEFT ARROW KEY
						case 38 : // UP ARROW KEY
							// ONLY CONSUME IF USING CTRL KEY
							if (e.ctrlKey) {
								e.preventDefault();
								e.stopPropagation();

								// MOVE FOCUS TO THE TAB
								$(".tab-button.active", element).focus();
							}
						break;

						case 33 : // PAGE UP KEY
							// ONLY CONSUME IF USING CTRL KEY
							if (e.ctrlKey) {
								e.preventDefault();
								e.stopPropagation();

								// GET THE PREVIOUS TAB
								var prevTab;
								if (parseInt(tab.attr("data-index")) == 0) { 
									prevTab = $(".tab-button[data-index='" + lastIndex + "']", element); // LAST TAB
								} else { 
									prevTab = $(".tab-button[data-index='" + (parseInt(tab.attr("data-index")) - 1) + "']", element); // PREVIOUS TAB
								} 

								// SWITCH THE TABS
								switchTabs(tab, prevTab);
							}
						break;

						case 34 : // PAGE DOWN KEY
							// ONLY CONSUME IF USING CTRL KEY
							if (e.ctrlKey) {
								e.preventDefault();
								e.stopPropagation();

								// GET THE NEXT TAB
								var nextTab;
								if (parseInt(tab.attr("data-index")) == lastIndex) { 
									nextTab = $(".tab-button[data-index='0']", element); // FIRST TAB
								} else { 
									nextTab = $(".tab-button[data-index='" + (parseInt(tab.attr("data-index")) + 1) + "']", element); // PREVIOUS TAB
								} 

								// SWITCH THE TABS
								switchTabs(tab, nextTab);
							}
						break;
					}
				}
			}

			function switchTabs(currTab, nextTab) {
				// SWITCH TAB
				$(currTab).removeClass("active").attr("aria-selected", "false").attr("tabindex", "-1");
				$(nextTab).addClass("active").attr("aria-selected", "true").attr("tabindex", "0").focus();

				// SWITCH PANEL
				var nextIndex = parseInt(nextTab.attr("data-index"));
				$("div.ui-widget.app.activeTabApp", element).removeClass("active-app");
				$("div.ui-widget.app.activeTabApp:eq(" + nextIndex + ")", element).addClass("active-app");
				$(".tab-panel", element).attr("aria-hidden", "true");
				$(".tab-panel[data-index='" + nextIndex + "']", element).attr("aria-hidden", "false");
			}

            $("div.tab-button:eq(0)",element).click();
            setTimeout(function(){
            	$("div.tab-button:eq(0)",element).click();
            }, 3000);
		});
	}
})(jQuery);

