div.script:
$(window).scroll(function () {
    var st = $(window).scrollTop();
    var dt = $(".hpDiv1").position().top;
    if (st >= dt) {
        $("header").css("background-color", "rgba(2,32,18,1)");
    }
    else {
        $("header").css("background-color", "rgba(2,32,18,.8)");
    }
});

