link.script:
        function ChangePage(PageURL, reverse) {
            if (reverse === undefined){
                reverse = false;
            }
            $.mobile.changePage(PageURL, { reverse: reverse });
        }

        $('.ui-page').live('pageinit', function (event) {
            ga('BBTracker.send', 'event', 'Mobile', 'Page', 'Page View');
        });

        //Add slashes on insert and update for quotes, double quotes, and backslashes
        function addslashes(str) {
            if (str !== undefined) {
                str = str.replace(/\\/g, '\\\\'); // this one must go before single and double quotes
                str = str.replace(/\'/g, '\\\'');
                str = str.replace(/\"/g, '\\\"');
                return str;
            }
            else return false;
        }

        function CallController(URL, Data, SuccessCallback, FailureCallback, IsSynchronous) {
            if (URL.URL) { // Passed as JSON string
                Data = URL.Data;
                SuccessCallback = URL.SuccessCallback;
                FailureCallback = URL.FailureCallback;
                URL = URL.URL;
            }
            $.ajax({
                type: "POST",
                url: URL,
                data: Data,
                async: (IsSynchronous == 1 ? false : true),
                contentType: "application/json; charset=utf-8",
                dataType: "json",
                cache: false,
                success: function (msg) {
                    var result = JSON.parse(msg.d);
                    if (result[0].success == "true") {
                        eval(SuccessCallback);
                    }
                    else {
                        eval(FailureCallback);
                    }
                }
            });
        }

        function CallControllerFailure(message) {
            if (typeof ajaxCall != 'undefined') { ajaxCall.abort(); }
            if (typeof WarnIfDirtyReclickTimeout != 'undefined') { clearTimeout(WarnIfDirtyReclickTimeout); }
            alert(message);
        }

