function googleTranslateElementInit() {
    new google.translate.TranslateElement({
        pageLanguage: 'en',
        layout: google.translate.TranslateElement.InlineLayout.SIMPLE
    }, 'google_translate_element');
}

            if (!window.hasOwnProperty('webkitSpeechRecognition')) {
                $(".GoogleSearch span").remove();
                $(".GoogleSearch input").css("width", "calc(100% - 30px)");
            }
            function startDictation() {

                if (window.hasOwnProperty('webkitSpeechRecognition')) {

                    var recognition = new webkitSpeechRecognition();

                    recognition.continuous = false;
                    recognition.interimResults = false;

                    recognition.lang = "en-US";
                    recognition.start();

                    $(".header_search_box")[0].placeholder = "Listening...";
                    $(".header_search_box")[1].placeholder = "Listening...";
                    recognition.onresult = function (e) {
                        $(".header_search_box")[0].placeholder = "";
                        $(".header_search_box")[0].value = e.results[0][0].transcript;
                        $(".header_search_box")[1].placeholder = "";
                        $(".header_search_box")[1].value = e.results[0][0].transcript;
                        recognition.stop();
                        $(".header_search_button")[0].click();
                    };

                    recognition.onerror = function (e) {
                        recognition.stop();
                    }

                    recognition.onend = function (e) {
                        $(".header_search_box")[0].placeholder = "Search...";
                        $(".header_search_box")[1].placeholder = "Search...";
                    }
                }
            }

