function getstatus(statdiv){
		$('#statusd').addClass('load');
			$.post("statusd.php", {queryString: statdiv}, function(data){
				if(data.length >0) {
					$('#statusd').fadeIn();
					$('#statusd').html(data);
					$('#statusd').removeClass('load');
				}
			 });
setTimeout ( "getstatus('statusd')", 30000 );
	}
function getstatusp(statdiv){
		$('#statusframe').addClass('load');
			$.post("status.php", {queryString: statdiv}, function(data){
				if(data.length >0) {
					$('#statusframe').fadeIn();
					$('#statusframe').html(data);
					$('#statusframe').removeClass('load');
				}
			 });
setTimeout ( "getstatusp('statusframe')", 30000 );
	}

