function togglemenus(dir){
if(dir=='left'){
if($('#menur').css('display') == 'none'){} else{
$('#menur').css('right','0');
$('#menur').animate({right:'-350px'}).fadeOut('5');
}
if($('#menu').css('display') == 'none'){
$('#menu').fadeIn('5').css('left','-350px');
$('#menu').animate({left:'0'});
$('#menu').attr('tabindex',0).focus();
} else{
$('#menu').css('left','0');
$('#menu').animate({left:'-350px'}).fadeOut('5');
}
} else{
if($('#menu').css('display') == 'none'){} else{
$('#menu').css('left','0');
$('#menu').animate({left:'-350px'}).fadeOut('5');
}
if($('#menur').css('display') == 'none'){
$('#menur').fadeIn('5').css('right','-350px');
$('#menur').animate({right:'0'});
$('#menur').attr('tabindex',0).focus();
} else{
$('#menur').css('right','0');
$('#menur').animate({right:'-350px'}).fadeOut('5');
}
}

}

function togglemenu(mid){
if($('#'+ mid).css('height') == '0px'){
$('#'+ mid).animate({'overflow-y':'', 'height':$('#'+ mid).get(0).scrollHeight});
$('.mmenusub:not(#'+ mid +')').animate({'height':'0px', 'overflow-y':'hidden'});
$('#'+ mid).attr('tabindex',1).focus();
} else{
$('#'+ mid).animate({'height':'0px', 'overflow-y':'hidden'});
$('#'+ mid).attr('tabindex',0).focus();
}
}

function togglemenur(mid){
if($('#'+ mid).css('height') == '0px'){
$('#'+ mid).animate({'overflow-y':'', 'height':$('#'+ mid).get(0).scrollHeight});
$('.mmenusubr:not(#'+ mid +')').animate({'height':'0px', 'overflow-y':'hidden'});
$('#'+ mid).attr('tabindex',1).focus();
} else{
$('#'+ mid).animate({'height':'0px', 'overflow-y':'hidden'});
$('#'+ mid).attr('tabindex',0).focus();
}
}

