function falert(fnumb,fmax){
var rmnng=fmax-fnumb;
$('#falertdiv').css( "width","100%" );
$('#falertdiv').css( "height","600px" );
$('#falertdiv').css( "top","50px" );
$('#falertdiv').css( "left","0" );
$('#falertdiv').fadeIn();
$('#falerttext').html('
Login Error!
If you forgot your password, click the
Question Mark beside the login box
to have your password emailed to you.
After '+ fmax +' failed attempts, you will be banned.
You have '+ rmnng +' attempts remaining
');
document.getElementById('faudio').play();
}

suptshow='Yes';
function showsuptsmsg(){
if(suptshow=='Yes'){
$('#suptidiv').animate({height: "400px"});
$('#suptmsgimg').css('display','none');
$('#suptmsgup').css('display','');
$('#suptstxt').css('display','inline-block');
$('#embvid').css('display','inline-block');
$('#suptmsgdontshow').css('display','none');
suptshow='No';
} else{
$('#embvid').css('display','none');
$('#suptstxt').css('display','none');
$('#suptidiv').animate({height: "0px"});
$('#suptmsgup').css('display','none');
$('#suptmsgimg').css('display','');
$('#suptmsgdontshow').css('display','');
$('#mbedvid').trigger("pause");
suptshow='Yes';
}
}

function setwatched(vid){
$.post("/includes/template/setwatched.php", {queryString: ""+ vid +""}, function(data){
$('#suptodiv').animate({height: "0px"});
$('#suptodiv').css('display', 'none');
});
}

