imgnumb=10;
imgbnumb=1;
function slide1Switch() {
        var $active = $("#imageslider1 img.activeimg");
if(imgbnumb==imgnumb){
        var $next = $("#imageslider1 img:first-child");
        imgbnumb=1;
} else{
        var $next = $active.next();
imgbnumb++;
}

        $active.removeClass('activeimg');
        $active.fadeOut('slow');

        $next.addClass('activeimg');
        $next.fadeIn('slow');


}

$(function() {
imgslider1int = setInterval( "slide1Switch()", 4000 );
});

