anns_count=1;
anns_total=1;
autocount=0;

function ann_updates(cnt,numb){
if(cnt=='up'){
anns_count++;
} else{
anns_count=anns_count-1;
}
if(anns_count>=anns_total||go_up=='down' && anns_count>1){go_up='down';} else{go_up='up';}
if(anns_count>anns_total){anns_count=anns_total;}
if(anns_count<1){anns_count=1;}
for(i=0;i<=anns_total;i++){
$('#anns_'+i).hide();
}
$('#anns_'+anns_count).fadeIn();
anns_div = document.getElementById('anns_sel');
anns_div.innerHTML= anns_count + '/1';
}
go_up='up';
if(anns_total>1){
autocycle= setInterval("ann_updates(go_up,anns_count)",4000);
}








