            function adjustTopLevelHeight()
            {
                if(topLevelHeightAdjusted)
                    return true;
                if(A_MENUS && A_MENUS.length > 0)
                {
                    var root = A_MENUS[A_MENUS.length-1].o_root;
                    if(root && root.a_children && root.a_children[0].getprop('direction') == 'vertical')
                        menu_shift_items(root);
                }
                topLevelHeightAdjusted = 1;
            }

            adjustTopLevelHeight();
