$(".group843850").colorbox({rel:'group843850', maxWidth: '80%'});
    $( window ).resize( function(){
        if( $('#cboxOverlay' ).css( 'visibility' ) ){
        $(".group843850").colorbox.resize({ innerWidth: '80%', scalePhotos: 'true'});
    }
});
var $gallery843850 = $("a[rel=group843850]").colorbox({maxWidth: "80%"});
$("a#openGallery843850").click(function(e){
    e.preventDefault();
    $gallery843850.eq(0).click();
});

