    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false,

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.JsMediaQueries();
            this.Header();
            this.Search();
            this.Translate();
            this.Schoollist();
            this.ChannelBar();
            this.Body();
            this.MovePasskeys();
            this.ModEvents();
            this.GlobalIcons();
            this.SocialIcons();
            this.Footer();
            if($(".hp").length) {
            	this.Slideshow();
                this.CheckSlideshow();
            }
            this.RsMenu();
            csGlobalJs.OpenInNewWindowWarning();

            $(window).load(function(){ _this.WindowLoad(); });
            $(window).resize(function(){ _this.WindowResize(); });
            $(window).scroll(function() { _this.WindowScroll(); });
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {
            this.setChannelBgPhoto();
            this.channelDropdowns();


        },

        "WindowResize": function() {
            this.JsMediaQueries();
            this.MovePasskeys();
            if($(".hp").length) {
                this.CheckSlideshow();
            }
        },

        "WindowScroll": function() {
        	if($(".hp").length) {
                this.CheckSlideshow();
            }
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop":

                break;
                case "768":

                break;
                case "640":

                break;
                case "480":

                break;
                case "320":

                break;
            }
        },

        "channelDropdowns": function() {
        	//BUILD AN ARRAY OF ELEMENTS
            var channelElements = [
                [//CHANNEL ONE
                    ['Who We Are ',4],
                    ['The Board',4],
                    ['Other Info',5],
                    ['/cms/lib/LA01907308/Centricity/Template/GlobalAssets/images///Channel Images/broad-street.jpg'],
