                ],
                [//CHANNEL SEVEN
                    ['General',8],
                    ['Resources',7],
                    ['Other',6],
                    ['/cms/lib/LA01907308/Centricity/Template/GlobalAssets/images///Channel Images/parentsstudents.jpg'],
                    ['CPSB students are provided with a multitude of opportunities in innovation and academic excellence. Improved curriculum through the effective use of technology, accountability, assessment, instruction, and professional development are impacted by multiple district projects.  Here you will find resources and services to support your experience in the Calcasieu Parish School Board.']
                ],
                [//CHANNEL EIGHT
                    ['',],
                    ['',],
                    ['',],
                    ['/cms/lib/LA01907308/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg'],
                    ['']
                ]
            ];

            //LOOP THROUGH CHANNELS THAT HAVE DROPDOWNS
            $(".sw-channel-item >a[aria-haspopup='true']").each(function(){
                if($("#navc-HP").length){
                    var channelNumber = $(this).parent().index()-1;
                } else {
                    var channelNumber = $(this).parent().index();
                }


                //CHECK TO SEE HOW MANY COLUMNS THIS CHANNEL WILL HAVE IN THE DROPDOWN
                //AND HOW MANY SECTIONS WILL BE IN EACH COLUMN
                var numColumns = 0;

                //LOOP THROUGH THE COLUMN ELEMENTS IN THE CHANNEL ARRAY
                for (i = 0; i <= 2; i++) { 
                    var columnTitle = channelElements[channelNumber][i][0].trim();
                    var numSections = channelElements[channelNumber][i][1];

                    if(columnTitle == ''){
                        //DO NOTHING

                    } else {
                        //ADD ONE TO THE numColumns VARIABLE EACH TIME THERE IS TEXT FOUND IN THE COLUMN TITLE
                        numColumns++;
                    }
                }

                var dropDownHTML = '';

                //ADD IMAGE & DESCRIPTION COLUMN
                var dropDownImage = channelElements[channelNumber][3][0];
                var dropDownDesc = channelElements[channelNumber][4][0];
                dropDownHTML += "
" +
                					"
"+
                                        "
" + dropDownDesc + ""+
                                    "" +
                                    "
"+
                                        ""+
                                    "" +
                                 "" +
                                "
";

                //ONLY PROCEED IF THERE IS MORE THAN 1 COLUMN
                if(numColumns > 1){
                    //ADD CLASS TO THE DROPDOWN FOR HOW MANY COLUMNS
                    $(this).parent().find(".sw-channel-dropdown").addClass("column-"+numColumns);

                    //LOOP THROUGH EACH COLUMN AND BUILD THE STRUCTURE FOR IT
                    for(i = 0; i < numColumns; i++){
                        var numSections = channelElements[channelNumber][i][1];
                        var sectionCode = '';

                        $(this).parent().find(".sw-channel-dropdown li:not(.tagged)").each(function(){
                            if($(this).index() < numSections){	
                                sectionCode += "
"+$(this).html()+"";
                                $(this).addClass("tagged");
                            }
                        })

                        //REMOVE SECTIONS THAT WERE ADDED TO NEW STRUCTURE SO WE CAN
                        //BEGIN OUR INDEX AT 0 AGAIN THE NEXT TIME THROUGH THE LOOP
                        $(".sw-channel-dropdown li.tagged").remove();

                        dropDownHTML += "
"+
                                            ""+
                                            "
"+channelElements[channelNumber][i][0].trim()+""+
                                                sectionCode+
                                            ""+
                                        "";
                    }

                    dropDownHTML += "";

                    //ADD DROPDOWN CODE INTO PAGE
                    $(this).parent().find(".sw-channel-dropdown").html(dropDownHTML);
                } else {
                    $(this).parent().addClass("normal-dd");
                }

            });

            // KEY MAPPING
			var csKeyCodes = { "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40, "tab": 9 };

            //CHANNEL BAR DROPDOWNS
            $(".column-item a").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":nth-child(2)")) {
                            // IS FIRST ITEM IN FIRST COLUMN
                            if($(this).parent().parent().parent().is(":first-child")){
                                // FOCUS LAST ITEM IN LAST COLUMN
                                $("li.sw-channel-item.hover").find("> a").focus();
                            } else {
                                // FOCUS LAST ITEM IN PREVIOUS COLUMN
                                $(this).parent().parent().parent().prev().find(".column-item:last-child > a").focus();
                            }

                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // IS LAST ITEM IN LAST COLUMN
                            if($(this).parent().parent().parent().is(":last-child")){
                                // FOCUS FIRST ITEM IN FIRST COLUMN
                                $("li.sw-channel-item.hover").find(".dropdown-column:first-child .column-item:nth-child(2) > a").focus();
                            } else {
                                // FOCUS FIRST ITEM IN LAST COLUMN
                                $(this).parent().parent().parent().next().find(".column-item:nth-child(2) > a").focus();
                            }

                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }

                        // IS LAST ITEM
                        /*if($(this).parent().is(":last-child")) {
                            //IS LAST ITEM IN LAST COLUMN
                            if($(this).parent().parent().parent().is(":nth-last-child(2)")){
                                // FOCUS FIRST ITEM IN FIRST COLUMN
                                $("li.sw-channel-item.hover").find(".dropdown-column:first-child .column-item:nth-child(2) > a").focus();
                            } else {
                                // FOCUS FIRST ITEM IN NEXT COLUMN
                                $(this).parent().parent().parent().next().find(".column-item:eq(1) > a").focus();
                            }
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }*/
                    break;

                    // CONSUME TAB KEY
                    case csKeyCodes.tab:
                        if(e.shiftKey){
                            e.preventDefault();
                            // IS FIRST ITEM
                            if($(this).parent().parent().parent().parent().parent().is(":first-child")) {
                                // FOCUS DROPDOWN BUTTON
                                $(this).parent().parent().parent().parent().parent().parent().find("> a").focus();
                            } else {
                                // FOCUS PREVIOUS ITEM
                                $(".sw-channel-item.hover").prev().find("> a").focus();
                            }
                        } else {
                            e.preventDefault();

                            $(".sw-channel-item.hover").next().find("> a").focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case csKeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM IN FIRST COLUMN
                        $("li.sw-channel-item.hover").find(".dropdown-column:first-child .column-item:nth-child(2) > a").focus();
                    break;

                    // CONSUME END KEY
                    case csKeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM IN LAST COLUMN
                        $("li.sw-channel-item.hover").find(".dropdown-column:nth-child(3) .column-item:last-child > a").focus();
                    break;

                    case csKeyCodes.esc:
                        $(".sw-channel-item.hover").find("> a").focus();
                    break;
                }
            });
        },

        "Translate": function() {
        	// ADD TRANSLATE
            var _this = this;

            $("#gb-translate-panel").delay(300).creativeTranslate({
                "type": 2,
                "advancedOptions": {
                    "brandedLayout": 1 // 1 = VERTICAL (TEXT UNDER SELECT), 2 = HORIZONTAL (TEXT BESIDE SELECT), 3 = NON-RESPONSIVE MEGA DROPDOWN
                }
            });

            $("#gb-translate-panel").css({
                "display": "none"
            });

            $("#gb-translate-toggle").hover(function() {
            	$(this).parent().addClass("hover");
            }, function() {
            	$(this).parent().removeClass("hover");
            });

            $(document).on("click keydown", "#gb-translate-toggle", function(e) {
            	if(_this.AllyClick(e)) {
                	e.preventDefault();

                    if($(this).attr("aria-expanded") == "false") {
                        $(this).attr("aria-expanded", "true");
                        $(this).parent().addClass("active");

                        $("#gb-translate-panel").css({
                            "display": "block"
                        });
                        setTimeout(function() {
                        	$("#gb-translate-panel").addClass("show-translate").attr("aria-hidden", "false");
                        }, 100);
                        $("#cs-branded-translate-label").attr({
                        	"aria-hidden": "false",
                            "tabindex": "0"
                        }).focus();
                        $(".goog-logo-link").attr({
                        	"aria-hidden": "false",
                            "tabindex": "0"
                        });
                    } else {
                        $(this).attr("aria-expanded", "false");
                        $(this).parent().removeClass("active");

                        $("#gb-translate-panel").removeClass("show-translate").attr("aria-hidden", "true");
                        $("#cs-branded-translate-label, .goog-logo-link").attr({
                        	"aria-hidden": "true",
                            "tabindex": "-1"
                        });

                        setTimeout(function() {
                            $("#gb-translate-panel").css({
                                "display": "none"
                            });
                        }, 100);
                    }
                }
            });

            $("#gb-search-toggle").focus(function() {
            	if($(".show-translate").length) {
                	$("#gb-translate-panel").css({
                            "display": "block"
                        });

            		$("#gb-translate-toggle").attr("aria-expanded", "false");

                    $("#gb-translate-panel").removeClass("show-translate").attr("aria-hidden", "true");
                    $("#cs-branded-translate-label, .goog-logo-link").attr({
                        "aria-hidden": "true",
                        "tabindex": "-1"
                    });
                    setTimeout(function() {
                        $("#gb-translate-panel").css({
                            "display": "none"
                        });
                    }, 100);

                    $("#gb-translate-toggle").parent().removeClass("active");
            	}
            });
            $(".cs-mystart-dropdown.schools .cs-selector").focus(function() {
            	if($(".show-translate").length) {
            		$("#gb-translate-toggle").attr("aria-expanded", "false");

                    $("#gb-translate-panel").removeClass("show-translate").attr("aria-hidden", "true");
                    $("#cs-branded-translate-label, .goog-logo-link").attr({
                        "aria-hidden": "true",
                        "tabindex": "-1"
                    });
                    setTimeout(function() {
                        $("#gb-translate-panel").css({
                            "display": "none"
                        });
                    }, 100);

                    $("#gb-translate-toggle").parent().removeClass("active");
            	}
            });
        },

        "DropdownActions": function(params) {
        	// FOR SCOPE
            var template = this;

            var dropdownParent = params.dropdownParent;
            var dropdownSelector = params.dropdownSelector;
            var dropdown = params.dropdown;
            var dropdownList = params.dropdownList;

            $(dropdownParent + " " + dropdownList + " a").attr("tabindex", "-1");

            // MYSTART DROPDOWN SELECTOR CLICK EVENT
            $(dropdownParent).on("click", dropdownSelector, function(e) {
                e.preventDefault();

                if($(this).parent().hasClass("open")){
                	$("+ " + dropdownList + " a").attr("tabindex", "-1");
                    $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                } else {
                	$(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden","false").slideDown(300, "swing");
                }
        	});

            // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownSelector, function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();

                        // IF THE DROPDOWN IS OPEN, CLOSE IT
                        if($(dropdownParent).hasClass("open")){
                        	$("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                        } else {
                        	$(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                                $(dropdownList + " li:first-child a", this).attr("tabindex", "0").focus();
                            });
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        if($("+ " + dropdown + " " + dropdownList + " a").length) {
                        	$("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        	$(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                        }
                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.down:
                    case template.KeyCodes.right:
                        e.preventDefault();

                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $("+ " + dropdown + " " + dropdownList + " li:first-child > a", this).attr("tabindex", "0").focus();
                    break;
                }
            });

            // MYSTART DROPDOWN LINK KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownList + " li a", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                    	if(e.shiftKey) {
                        	e.preventDefault();

                        	// FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:last-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME ESC KEY
                    case template.KeyCodes.esc:
                    	e.preventDefault();

                        // FOCUS DROPDOWN BUTTON
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        $(this).closest(dropdownParent).removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                   break;
                }
            });

            $(dropdownParent).mouseleave(function(e) {
            	if(e.target.nodeName.toLowerCase() !== "select") {
					$(dropdownList + " a", this).attr("tabindex", "-1");
					$(dropdownSelector, this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
            	}
            }).focusout(function() {
                var thisDropdown = this;

                setTimeout(function () {
                    if(!$(thisDropdown).find(":focus").length) {
                        $(dropdownSelector, thisDropdown).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    }
                }, 800);
            });
        },

