        "ChannelBar": function() {
            $(".sw-channel-item").unbind("hover");
            $(".sw-channel-item").hover(function(){
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
                $(this).parent().addClass("remove-active");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
                $(this).parent().removeClass("remove-active");
            });

            $(".sw-channel-item").each(function(){
            	if($(".sw-channel-dropdown li", this).length == 0) {
                	$(this).addClass("no-dropdown");
                }
            });

            $("li.sw-channel-item").each(function() {
            	var thisChannelIndex = $(this).index();
                if(!$("#navc-HP").length) {
                	thisChannelIndex = thisChannelIndex + 1;
                }

                $(this).addClass("cs-channel-num"+thisChannelIndex);
            });
        },

        "Body": function() {
        	var _this = this;

            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

            // ADD READ FULL STORY BUTTON TO EACH HEADLINE
            $(".ui-widget.app.headlines").each(function() {
                $(".ui-article", this).each(function() {
                	var headlineTitle = $("h1 a",this).text();
                    var headlineURL = $("h1 a", this).attr("href");
                    $(".ui-article-description", this).append('Read more about ' + headlineTitle + '');
                });
            });

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $("div.ui-widget.app .ui-widget-detail img")
                .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
                .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            if($(".hp").length) {
                $(".hp-content").each(function() {
                    var thisCont = this;
                    if(!$(".ui-widget.app",thisCont).length) {
                        $(thisCont).remove();
                    }
                });

                $(".hp-content .ui-widget.app.headlines").each(function() {
                    var thisHeadlines = this;
                    if($(thisHeadlines).find(".more-link").length) {
                        $(thisHeadlines).find(".ui-widget-header").addClass("has-btns");
                        $(thisHeadlines).find(".more-link").appendTo($(thisHeadlines).find(".ui-widget-header.has-btns"));
                    }
                });

                $(".hp-content .ui-widget.app.upcomingevents").each(function() {
                    var thisEvents = this;
                    if($(thisEvents).find(".view-calendar-link").length) {
                        $(thisEvents).find(".ui-widget-header").addClass("has-btns");
                        $(thisEvents).find(".view-calendar-link").appendTo($(thisEvents).find(".ui-widget-header.has-btns"));
                    }
                });

                // SCROLL BUTTON
