                });

                $(".hp-content.two .ui-widget.app.navigation").each(function() {
                	var thisNavApp = this;
                    $(thisNavApp).parent().parent().addClass("cs-nav-app").attr("tabindex","0");
                });

                $(".cs-nav-app .ui-widget-detail").attr("aria-hidden","true").fadeOut();

                $(".cs-nav-app").hover(function() {
                    $(this).addClass("hover");
                    $(".ui-widget-detail",this).attr("aria-hidden","false").delay(100).fadeIn();
                }, function() {
                    $(this).removeClass("hover");
                    $(".ui-widget-detail",this).attr("aria-hidden","true").fadeOut();
                });

                if($(window).width() > 1023) {
                    $(".cs-nav-app").focus(function() {
                        if(!$(this).hasClass("hover")) {
                            $(this).addClass("hover");
                            $(".ui-widget-detail",this).attr("aria-hidden","false").fadeIn();
                        } else {
                            $(this).removeClass("hover");
                            $(".ui-widget-detail",this).attr("aria-hidden","true").fadeOut();
                        }
                    });
                }

                $(window).resize(function() {
                	if($(window).width() > 1023) {
                        $(".cs-nav-app").focus(function() {
                            if(!$(this).hasClass("hover")) {
                                $(this).addClass("hover");
                                $(".ui-widget-detail",this).attr("aria-hidden","false").fadeIn();
                            } else {
                                $(this).removeClass("hover");
                                $(".ui-widget-detail",this).attr("aria-hidden","true").fadeOut();
                            }
                        });
                    }
                });
            }

            if(!$(".hp-content.one .ui-widget.app").length) {
            	$(".hp-content.one").remove();
