        "ModEvents": function() {
            $(".ui-widget.app.upcomingevents").modEvents({
				columns     : "no",
				monthLong   : "yes",
				dayWeek     : "yes"
			});

			$(".ui-widget.app.upcomingevents .ui-article").each(function() {
				if (!$("h1.ui-article-title.sw-calendar-block-date", this).length){
					var moveArticle = $(this).html();
					$(this).parent().prev("li").find(".ui-article").append(moveArticle);
					$(this).parent().remove();
				}

                $(this).find(".sw-calendar-block-date").wrapInner("
");
                $(this).find(".joel-month").prependTo($(this).find(".sw-calendar-block-date"));
                $(this).find(".joel-day").prependTo($(this).find(".sw-calendar-block-date"));

                // ADD ZERO TO DAY IF LESS THAN 10
            	$(".jacinda-date", this).text(((parseInt($(".jacinda-date", this).text()) < 10) ? "0" : "") + $(".jacinda-date", this).text());
			});

            $(".ui-widget.app.upcomingevents .ui-article .ui-article-description").each(function() {
                var thisDesc = this;
                if(!$(thisDesc).find(".sw-calendar-block-time").length) {
                    $('All Day').prependTo(thisDesc);
                }
            });
        },

