						"url": "https://www.office.com",
						"target": "_blank"
					},
                    {
						"image": "/cms/lib/LA01907308/Centricity/Template/GlobalAssets/icons/classlink.png",
						"showText": true,
						"text": "Launchpad",
						"url": "http://launchpad.cpsb.org",
						"target": "_blank"
					}
				],
				"siteID"        : "4",
				"siteAlias"     : "cpsb",
				"calendarLink"  : "/Page/2",
				"contactEmail"  : "lisa.mullett@cpsb.org",
				"allLoaded"     : function(){
                	// SET POSITION
                	var contentTop;
                    if($(".hp").length) {
                    	contentTop = $("#hp-slideshow").offset().top;
                        contentTop = contentTop + 57;
                        var windowHeight = $(window).height();
                        var channelHeight = $("#gb-channel-list-outer").outerHeight(true);
                        var headerHeight = $("#gb-header-outer").outerHeight(true);
                        var iconsHeight = $("#gb-global-icons-outer").outerHeight(true);
                        var totalHeight = channelHeight + headerHeight + iconsHeight;
                        var iconsHeightHalf = iconsHeight / 2;


                        if(totalHeight > windowHeight) {
                        	contentTop = 
                            $("#gb-global-icons-outer").css({
                                "top": "50%",
                                "margin-top": "-" + iconsHeightHalf + "px"
                            });
                        } else {
                            $("#gb-global-icons-outer").css({
                                "top": contentTop + "px"
                            });
                        }
                    } else if($(".sp").length) {
                    	contentTop = $("#sp-content").offset().top;
                        $("#gb-global-icons-outer").css({
                        	"top": contentTop + "px"
                        });
                    } else if($(".spn").length) {
                    	contentTop = $("#spn-content").offset().top;
                        $("#gb-global-icons-outer").css({
                        	"top": contentTop + "px"
                        });
                    }

                    // SLIDEOUT
                    $(".cs-global-icons li").each(function() {
                    	var thisIconLi = this;
                        var initWidth = $(this).width();

                        $(".cs-global-icon",thisIconLi).css({
                            "left": "calc(100% - 53px)"
                        });

                        $(".cs-global-icon",thisIconLi).hover(function() {
                        	$(".cs-global-icon",thisIconLi).css({
                            	"left": "0px"
                            });
                        }, function() {
                            $(".cs-global-icon",thisIconLi).css({
                                "left": "calc(100% - 53px)"
                            });
                        });

                        $(".cs-global-icon",thisIconLi).focus(function() {
                        	$(".cs-global-icon",thisIconLi).css({
                            	"left": "0px"
                            });
                        });
                        $(".cs-global-icon",thisIconLi).focusout(function() {
                        	$(".cs-global-icon",thisIconLi).css({
                                "left": "calc(100% - 53px)"
                            });
                        });
                    });

                    $(".cs-global-icon").hover(function() {
                    	$(this).parent().parent().addClass("hover");
                    }, function() {
                    	$(this).parent().parent().removeClass("hover");
                    });
                }
			});
        },

