            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(2)").html().replace("|", "")) + '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(1)").html().replace("|", "")) + '';
            $(".gb-schoolwires-footer.links").append('' + schoolwiresLinks + '');
            $(".gb-schoolwires-footer.copyright").append($("#sw-footer-copyright").html());

            if("Job Openings" == "" || "Job Openings" == " ") {
            	$(".gb-footer-link.one").remove();
            }
            if("Registration Login" == "" || "Registration Login" == " ") {
            	$(".gb-footer-link.two").remove();
            }


            	var loginUrl = $(".sw-mystart-button.signin a").attr("href");
            	$(".gb-footer-link.login").attr("href", loginUrl);

                if($(".sw-mystart-button.register").length) {
                	var registerUrl = $(".sw-mystart-button.register a").attr("href");;
                    $(".gb-footer-link.login").after('Register')
                }

