        "Slideshow": function() {
        	// FOR SCOPE
            var _this = this;

            if($("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery").length) {
                var mmg = eval("multimediaGallery" + $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
                mmg.props.defaultGallery = false;

                $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first").csMultimediaGallery({
                    "efficientLoad" : true,
                    "imageWidth" : 700,
                    "imageHeight" : 525,
                    "mobileDescriptionContainer": [480, 320], // [960, 768, 640, 480, 320]
                    "galleryOverlay" : false,
                    "linkedElement" : [],  // ["image", "title", "overlay"]
                    "playPauseControl" : true,
                    "backNextControls" : true,
                    "bullets" : false,
                    "thumbnails" : false,
                    "thumbnailViewerNum": [3, 4, 3, 3, 2], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                    "autoRotate" : true,
                    "hoverPause" : true,
                    "transitionType" : "fade",
                    "transitionSpeed" : 3,
                    "transitionDelay" : 5,
                    "fullScreenRotator" : false,
                    "fullScreenBreakpoints" : [960], // NUMERICAL - [960, 768, 640, 480, 320]
                    "onImageLoad" : function(props) {}, // props.element, props.recordIndex, props.mmgRecords
                    "allImagesLoaded" : function(props) {}, // props.element, props.mmgRecords
                    "onTransitionStart" : function(props) {
                        if($(".mmg-description", props.element).length && !$(".mmg-description .mmg-description-inner", props.element).length) {
                            $(".mmg-description", props.element).wrapInner("
");
                        }
                        if($(".mmg-description-link.read-more", props.element).length || $(".mmg-description-link.watch-video").length) {
                            $(".mmg-description-link.read-more").attr("aria-label","Read More").addClass("calcasieu-icon-font").html("");
                            $(".mmg-description-link.watch-video").attr("aria-label","Watch Video").addClass("calcasieu-icon-font").html("");
                            $(".mmg-description", props.element).addClass("more-link-active");
                        } else {
                            $(".mmg-description", props.element).removeClass("more-link-active");
                        }
                    }, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
