        "Search": function() {
            // FOR SCOPE
            var _this = this;

            $("#gb-search-form").css({
                "display": "none"
            });

            $("#gb-search-toggle").hover(function() {
            	$(this).parent().addClass("hover");
            }, function() {
            	$(this).parent().removeClass("hover");
            });

            $(document).on("click keydown", "#gb-search-toggle", function(e) {
            	if(_this.AllyClick(e)) {
                	e.preventDefault();

                    if($(this).attr("aria-expanded") == "false") {
                        $(this).attr("aria-expanded", "true");
                        $(this).parent().addClass("active");

                        $("#gb-search-form").css({
                            "display": "block"
                        });

                        setTimeout(function() {
                        	$("#gb-search-form").addClass("show-search").attr("aria-hidden", "false");
                        }, 100);
                        $("#gb-search-input").attr({
                        	"aria-hidden": "false",
                            "tabindex": "0"
                        }).focus();
                    } else {
                        $(this).attr("aria-expanded", "false");
                        $(this).parent().removeClass("active");

                        $("#gb-search-form").removeClass("show-search").attr("aria-hidden", "true");
                        $("#gb-search-input").attr({
                        	"aria-hidden": "true",
                            "tabindex": "-1"
                        });

                        setTimeout(function() {
                            $("#gb-search-form").css({
                                "display": "none"
                            });
                        }, 100);
                    }
                }
            });

            $(document).on("keyup", function() {
            	if($("#gb-search-form").attr("aria-hidden") == "false" && !$("#gb-search-form").find(":focus").length && !$("#gb-search-toggle").is(":focus")) {
                	$("#gb-search-toggle").attr("aria-expanded", "false");
                    $("#gb-search-form").removeClass("show-search").attr("aria-hidden", "true");
                    $("#gb-search-input").attr({
                        "aria-hidden": "true",
                        "tabindex": "-1"
                    });

                    setTimeout(function() {
                        $("#gb-search-form").css({
                            "display": "none"
                        });
                    }, 100);

                    $("#gb-search-toggle").parent().removeClass("active");
                }
            });

            $("#gb-search-input").focusout(function() {
            	setTimeout(function() {
                    if(!$("#gb-search-input").find(":focus").length && !$("#gb-search-toggle").is(":focus") && !$("#gb-search-button").is(":focus")) {
                        $("#gb-search-toggle").attr("aria-expanded", "false");
                        $("#gb-search-form").attr("aria-hidden", "true").removeClass("show-search");

                        setTimeout(function() {
                            $("#gb-search-form").css({
                                "display": "none"
                            });
                        }, 100);

                        $("#gb-search-toggle").parent().removeClass("active");
                    }
                }, 500);
            }).keydown(function(e) {
            	if(e.keyCode == _this.KeyCodes.esc) {
                	e.preventDefault();

                    $("#gb-search-toggle").attr("aria-expanded", "false");
                    $("#gb-search-form").attr("aria-hidden", "true").removeClass("show-search");

                    setTimeout(function() {
                        $("#gb-search-form").css({
                            "display": "none"
                        });
                    }, 100);

                    $("#gb-search-toggle").parent().removeClass("active");
                }
            });

			$("#gb-search-form").submit(function(e){
				e.preventDefault();

				if($("#gb-search-input").val() != "SEARCH") {
                	if($("#gb-search-input").val() != "") {
						window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                    }
                }
			});

			$("#gb-search-input").focus(function() {
				if($(this).val() == "SEARCH") {
					$(this).val("");
				}
			}).blur(function() {
				if($(this).val() == "") {
					$(this).val("SEARCH");
				}
			});
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },

        "GatherRSMenuLinks": function(element) {
        	var links = [];

        	$(element).each(function() {
            	links.push({ "text": $(this).text(), "url": $(this).attr("href") });
            });

            return links;
        }
    };

