div.script:
if( window['jQuery'] ) {
    ( function( $ ) {
        $( document ).bind( 'gform_post_render', function() {  
            var $firstError = $( 'li.gfield.gfield_error:first' );
            if( $firstError.length > 0 ) {
                $firstError.find( 'input, select, textarea' ).eq( 0 ).focus();
                document.body.scrollTop = $firstError.offset().top;
            }
        } );
    } )( jQuery );
}

