                .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
                .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home");

            // ADJUST MORE LINKS
            $("a.more-link span").text("View All");

            // USE CHANNEL NAME FOR PAGELIST NAV HEADER IF ONE IS NOT PRESENT
            if(!$("div.sp.column.one .ui-widget-header h1").length) {
                $("div.sp.column.one .ui-widget-header").append("
");
            }

            //--> Check for Emergency Announcement
            if($('#gb-emergency-outer .ui-articles > li').length) {
            	$('#gb-emergency-outer').show().attr('aria-hidden', 'false');

                //--> Add Emergency Image
                var showEImg = true;
                var eSrc = $.trim("/cms/lib/MA02212540/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg");
                if(showEImg) {
                    var srcSplit = eSrc.split("/");
                    var srcSplitLen = srcSplit.length;
                    if((eSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                        $("#gb-emergency-outer .gb-section").prepend('');
                    } else {
                        $("#gb-emergency-outer .gb-section").prepend('');
                    }
                }

            }

            //--> To content btn
            $('#cj-to-content').click(function(e){
            	e.preventDefault();
                var offset = $('#hp-content-outer').offset();
                $("html, body").animate({ scrollTop: (offset.top - $('#gb-channel-list-outer').height()) }, 1000, function() {
                    $('#hp-content-outer a').first().focus();
                });
            });

            //--> If Homepage Build Triangles
            if($('#youtube-video').length) {

            	$('#video-triangles').height(($(window).height() - $('#gb-header').height()) + 'px');

                //--> Draw canvas triangle
                var canvasTop = document.getElementById("video-triangle-top");
                if (canvasTop.getContext) {
                    var ctx = canvasTop.getContext('2d');

                    ctx.beginPath();
                    ctx.moveTo(0, 0);
                    ctx.lineTo(260, 0);
                    ctx.lineTo(0, 700);
                    ctx.fillStyle = "rgba(255,131,0,0.2)";
                    ctx.fill();
                }

                //--> Draw canvas triangle
                var canvasBottom = document.getElementById("video-triangle-bottom");
                if (canvasBottom.getContext) {
                    var ctx = canvasBottom.getContext('2d');

                    ctx.beginPath();
                    ctx.moveTo(0, 722);
                    ctx.lineTo(437, 722);
                    ctx.lineTo(0, 0);
                    ctx.fillStyle = "rgba(255,131,0,0.2)";
                    ctx.fill();
                }

