                            $(article).html("
" + $(".ui-article-thumb", article).html() + "

" + $('.ui-article-title', article).html() + "
" + $('.ui-article-description', article).html()  + "");

                    	}
                    });
                });

                //--> Headlines Scroller Slide Up Content
                $('.ui-article-thumb').hover(function(){
                	$(this).addClass('hover');
                	$('.cj-hf-overlay', this).attr('aria-hidden', 'false').css('top', '0');
                }, function(){
                	$(this).removeClass('hover');
                	$('.cj-hf-overlay', this).attr('aria-hidden', 'true').css('top', '100%');
                })
                $('.hp .ui-article-title a').focus(function(){
                	$(this).closest('.ui-article-thumb').addClass('hover');
                	$(this).closest('.cj-hf-overlay').attr('aria-hidden', 'false').css('top', '0');
                }).blur(function(){
                	$(this).closest('.ui-article-thumb').removeClass('hover');
                	$(this).closest('.cj-hf-overlay').attr('aria-hidden', 'true').css('top', '100%');
                });



