            //--> Upcoming Events
            function modEvents(){
                if (!$(".upcomingevents .joel-month").length) {
                    $(".upcomingevents").modEvents({
                        columns     : "yes",
                        monthLong   : "yes",
                        dayWeek     : "yes"
                    });
                    eventsByDay(".upcomingevents .ui-articles");
                }
            }          

            function eventsByDay(container) {
                var countLi = 0;
                $(".ui-article", container).each(function(num, article){
                    if (!$(article).find("h1.ui-article-title.sw-calendar-block-date").size()){
                        var moveArticle = $(article).html();
                        $(article).parent().prev().children().children().next().append(moveArticle);
                        $(article).parent().remove();


                    };

                    //--> Flex it and move day
                    $('.upcoming-column.left', article).addClass('cs-fc cs-fw').append($('.joel-day', article));

                });

                tabbedUE();
            }

            function tabbedUE() {

            	//--> Search Regions
                $('.region').each(function(k, region){

                    //--> Check for Upcoming events app
                    if($('.upcomingevents', region).length) {

                        //--> go through Apps
                        $('.upcomingevents', region).each(function(num, app){

                            //--> Get width and reduce articles on smaller views
                            if($(app).width() < 1000 && $(window).width() > 1024) {

                                $(app).addClass('articles-4');

                            }

                        	//--> Remove any li's over 6
                            $('.ui-articles > li', app).each(function(index, li){
                            	if(!$(app).hasClass('articles-4')) {
                                    if(index > 5) {
                                        $(li).remove();
                                    }
                                } else {
                                	if(index > 3) {
                                        $(li).remove();
                                    }
                                }

                                //--> Adde More Button
                                if($('.upcoming-column.right p', li).length > 2) {

                                    $('.upcoming-column.right', li).append('
More')

                                    $('.upcoming-column.right p', li).each(function(value, event){
                                        if(value > 1) {
                                            $(event).addClass('extra').hide().attr('aria-hidden', 'true');
                                        }
                                    });

                                }

                            });

                        	if(num == 0) {

                                //--> Build Tab container in first app instance
                                $(app).addClass('cj-tabbed-calendars').attr('id', "cj-tabbed-calendar-" + k);


                                //--> Create Tab container
                                $('.ui-widget-header', app).addClass('cj-calendar-tabs cs-fc').html("
" + $('.ui-widget-header h1', app).text() + "");

                                //--> Move View Calendar button to header
                                $('.ui-widget-header', app).append($('a.view-calendar-link', app));

                                //--> Create Panels
                                $('.ui-widget-detail', app).addClass('cj-calendar-panels');
                                $('.ui-articles', app).wrap("
");

                        	} else if(num != (app.length - 1)) {

                                //--> Remove Extra view calendar links
                                $('a.view-calendar-link', app).remove();

                                //--> Add Tab
                                $('.cj-tab-wrapper', region).append("" + $('.ui-widget-header h1', app).html() + "");

                                //--> Add Panel
                                $('.cj-calendar-panels', region).append("
" + $('.ui-widget-detail', app).html() + "");

                                //--> Remove app
                                $(app).remove();

                            } else {

                                //--> Remove Extra view calendar links
                                $('a.view-calendar-link', app).remove();

                                //--> Add Tab
                                $('.cj-tab-wrapper', region).append("" + $('.ui-widget-header h1', app).html() + "");

                                //--> Add Panel
                                $('.cj-calendar-panels', region).append("
" + $('.ui-widget-detail', app).html() + "");

                                //--> Remove app
                                $(app).remove();

                            }

                        });

                    }

                });

                $('.gb-column-more').unbind().click(function(){
                	if(!$(this).hasClass('open')) {
                		$(this).addClass('open');
                        $(this).siblings('.extra').slideDown().attr('aria-hidden', 'false');
                    } else {
                		$(this).removeClass('open');
                        $(this).siblings('.extra').slideUp().attr('aria-hidden', 'true');
                    }
                });
            }

            if($('.cj-tabbed-calendars').length) {

                //--> Draw canvas triangle
                var calendarTriangle = document.getElementsByClassName("calendar-tab-triangle");
                $.each(calendarTriangle, function(num, calendarTriangle){
                    if (calendarTriangle.getContext) {

                        //--> Get context
                        var ctx = calendarTriangle.getContext('2d');


                        //--> Start the Path
                        ctx.beginPath();

                        if($(calendarTriangle).hasClass('right')) {

                            //--> Draw Right Triangle
                            ctx.moveTo(0, 0);
                            ctx.lineTo(20, 0);
                            ctx.lineTo(0, 50);

                        } else if($(calendarTriangle).hasClass('border')) {

                            //--> Draw Border Triangle
                            ctx.moveTo(20, 0);
                            ctx.lineTo(21, 0);
                            ctx.lineTo(21, 50);
                            ctx.lineTo(0, 50);

                        } else {

                            //--> Draw Left Triangle
                            ctx.moveTo(20, 0);
                            ctx.lineTo(20, 50);
                            ctx.lineTo(0, 50);

                        }

                        //--> Add Some Color

                        if($(calendarTriangle).hasClass('hover')) {
                            ctx.fillStyle = "#efefef";
                        } else {
                            ctx.fillStyle = "#fff";
                        }

                        if($(calendarTriangle).hasClass('border')) {
                            ctx.fillStyle = "#a1a1a1";
                        }

                        //--> Fill Triangle
                        ctx.fill();

                        $('.cj-tab').click(function(){

                            //--> Check if tab is already active
                            if(!$(this).hasClass('active')) {

                                //--> Get some Variables
                                var panel = $(this).attr('aria-controls'),
                                    activeP = $(this).siblings('.active').attr('aria-controls');

                                $('#' + activeP).removeClass('active').attr('aria-hidden', 'true').hide();
                                $(this).siblings('.active').removeClass('active').attr('aria-expanded', 'false');
                                $(this).addClass('active').attr('aria-expanded', 'true');
                                $('#' + panel).show().attr('aria-hidden', 'false');

                            }

                        }).focus(function(){
                        	$(this).click();
                        }).unbind('keydown').keydown(function(e){

                        	var c = e.which;

                            if(c == KeyCodes.left || c == KeyCodes.right || c == KeyCodes.down) {

                                e.preventDefault();

                            	var next = $(this).next(),
                                	prev = $(this).prev();

                                if(c == KeyCodes.right && next != undefined) {
                                	next.focus().click();
                                } else if(c == KeyCodes.left && prev != undefined) {
                                	prev.focus().click();
                                } else if(c == KeyCodes.down || c == KeyCodes.enter) {
                                	$('.cj-panel.active a').first().focus();
                                }

                            }

                        });

                    }
            	});
            }


            //--> Update View Calendar Link Text
            $('a.view-calendar-link span').text('VIEW FULL CALENDAR');
        },

