                $("#gb-channel-list #sw-channel-list-container li.sw-channel-item").each(function(index){

                    // LOWERCASE THE CHANNEL NAME
                    var channelText = $("> a span", this).text().toLowerCase();
                    var channelTextReg = $("> a span", this).text();

					if($('ul', this).hasClass('dropdown-hidden')) {
                    	$('a', this).attr('aria-haspopup', 'false');
                    }

                    //REPLACE ANY SECTION THAT CONTAINS & WITH AND
                    $(".sw-channel-dropdown > li > a > span", this).text(function () {
                        return $(this).text().replace("&", "and"); 
                    });

                    // SET HOME AND CALENDAR CLASSES ON HOME AND CALENDAR CHANNELS
                    if(channelText == "home") {
                        $(this).addClass("home");
                    } else if(channelText == "calendar") {
                        $(this).addClass("calendar");
                    }

                    var useMenu = 0; 

                    //IGNORE HOME AND CALENDAR CHANNELS //--> Skip if there is no caption
                    if(!$(this).hasClass('home') && !$(this).hasClass('calendar')) {
                        // SET THE CHANNELS INDEX
                        //var i = ($("#sw-channel-list-container li.sw-channel-item.home").length) ? $(this).index() - 2 : $(this).index();

                        var i = $(this).index();

                        if($("#sw-channel-list-container li.sw-channel-item.home").length && $("#sw-channel-list-container li.sw-channel-item.calendar").length) {
                        	i = $(this).index() - 2;
                        }

                        if($("#sw-channel-list-container li.sw-channel-item.home").length && !$("#sw-channel-list-container li.sw-channel-item.calendar").length || !$("#sw-channel-list-container li.sw-channel-item.home").length && $("#sw-channel-list-container li.sw-channel-item.calendar").length) {
                        	i = $(this).index() - 1;
                        }

                        // ADD CHANNEL CATEGORIES
                        var categoryColumn = '
';
                        var textNums = ["one", "two", "three", "four", "five", "six", "seven", "eight", "nine", "ten", "eleven", "twelve", "thirteen", "fourteen", "fifteen", "sixteen", "seventeen", "eighteen", "nineteen", "twenty"];
                        var channelCats = megaMenuData[i].channelCats;
                        for(var c in channelCats) {
                            if(channelCats[c].channelCatHeader != "") {
                                var catSections = channelCats[c].channelCatSections.split(",");
                                if(c < 10){
                                	categoryColumn += '
';
                                } else {
                                	categoryColumn += '
';
                                }
                                categoryColumn += '
' + channelCats[c].channelCatHeader + '';
                                categoryColumn += '';
                                for(var s in catSections) {
                                    $("> .sw-channel-dropdown li", this).each(function(){
                                        if($("a span", this).text() == jQuery.trim(catSections[s])){
                                            //categoryColumn += '
' + jQuery.trim(catSections[s]) + '';
                                            categoryColumn += '
' + jQuery.trim(catSections[s]) + '';
                                            } else {
                                                categoryColumn += '>' + jQuery.trim(catSections[s]) + '';
                                            }
                                        }
                                    });

                                }
                                categoryColumn += 	'';
                                categoryColumn += '';
                            }    
                        }
                        categoryColumn +=	'';

                        if(megaMenuData[i].channelCaption != ''){
                            // ADD MEGA CHANNEL CLASS
                            $(this).addClass('mega-channel');
                            $(this).addClass("Channel-" + index);
						}

                        // ADD STRUCTURE TO THE DOM
                        if(megaMenuData[i].channelCaption != ""){

                        	var socialHTML = '
';

                            $('#gb-social-list li').each(function(index, social){
                            	socialHTML += $(social).html();
                            });

                            socialHTML += "";

                            var captionColumn = '
' + jQuery.trim(megaMenuData[i].channelCaption) + '';
                            $(".sw-channel-dropdown", this).html("





" + $('> a span', this).text() + "" + captionColumn + "
" + socialHTML + "
");

                        }
                        // ADD CHANNEL NAME
                        //var channelColumn = '
' + channelTextReg + '';
                        //$(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-one", this).append(channelColumn);

                        // ADD CAPTION
                        //$(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-one", this).append(captionColumn);

                        // ADD IMAGE
                        var imgColumn = '';
                        var imgSrc = jQuery.trim(megaMenuData[i].channelImg);
                        var srcSplit = imgSrc.split("/");
                        var srcSplitLen = srcSplit.length;
                        if(imgSrc != "" && srcSplit[srcSplitLen - 1] != "default-man.jpg") {
                            imgColumn = '';
                        }

                        $(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-one", this).html(imgColumn);
                        $(".sw-channel-dropdown .mega-columns .mega-menu-bottom", this).append(categoryColumn);

                        /*var catLength = $(".mega-channel-cat", this).length;
                        if(catLength > 3) {
                            $(".mega-channel-cat:last-child", this).addClass("two-row");
                            $(".mega-channel-cat:nth-child(3)", this).addClass("two-row");
                        }*/

                    }

                    if($(".mega-channel-cat", this).length == 3) {
                        $(".sw-channel-dropdown", this).css("width","700");
                        $(this).addClass("col-count-3");
                    }
                    if($(".mega-channel-cat", this).length == 4) {
                        $(".sw-channel-dropdown", this).css("width","930");
                         $(this).addClass("col-count-4");
                    }


                    $('.mega-channel .sw-channel-dropdown a').unbind();
                    $('.mega-channel .mega-channel-cat a').keydown(function(e){

                    	var c = e.which,
                        	a = parseInt($(this).attr('data-link')),
                            b = parseInt($(this).attr('data-column'));

                        if(c == KeyCodes.down || c == KeyCodes.up || c == KeyCodes.left || c == KeyCodes.right) {
                            e.preventDefault();
                            if(c == KeyCodes.down && !$('.mega-channel.hover .mega-channel-cat a[data-link="'+ (a + 1) +'"][data-column="'+ b +'"]').length) {
                                $('.mega-channel.hover .mega-channel-cat a[data-link="1"][data-column="'+ b +'"]').focus();
                            } else if(c == KeyCodes.down) {
                                $('.mega-channel.hover .mega-channel-cat a[data-link="'+ (a + 1) +'"][data-column="'+ b +'"]').focus();
                            } else if(c == KeyCodes.up && a == 1) {
                                $('.mega-channel.hover > a').focus();
                            } else if(c == KeyCodes.up) {
                                $('.mega-channel.hover .mega-channel-cat a[data-link="'+ (a - 1) +'"][data-column="'+ b +'"]').focus()
                            } else if(c == KeyCodes.right && !$('.mega-channel.hover .mega-channel-cat a[data-link="'+ a +'"][data-column="'+ (b + 1) +'"]').length) {
                                $('.mega-channel.hover .mega-channel-cat a[data-link="'+ a +'"][data-column="1"]').focus();
                            } else if(c == KeyCodes.right) {
                                $('.mega-channel.hover .mega-channel-cat a[data-link="'+ a +'"][data-column="'+ (b + 1) +'"]').focus();
                            } else if(c == KeyCodes.left && !$('.mega-channel.hover .mega-channel-cat a[data-link="'+ a +'"][data-column="'+ (b - 1) +'"]').length) {
                                if(!$('.mega-channel.hover .mega-channel-cat a[data-link="'+ a +'"][data-column="3"]').length) {
                                    $('.mega-channel.hover .mega-channel-cat a[data-link="'+ a +'"][data-column="2"]').focus();
                                } else {
                                    $('.mega-channel.hover .mega-channel-cat a[data-link="'+ a +'"][data-column="3"]').focus();
                                }
                            } else if(c == KeyCodes.left) {
                                $('.mega-channel.hover .mega-channel-cat a[data-link="'+ a +'"][data-column="'+ (b - 1) +'"]').focus();
                            }
                        }

                    });

                });


                if($('html').hasClass('ie11')) {

                	$('.sw-channel-item.mega-channel').each(function(index, channel){
                    	var channelP = $(channel).position();
                        var barW = $(channel).parent().width();

                        $('.sw-channel-dropdown', channel).css({
                        		width : barW + 'px',
                                left : '-' + channelP.left + 'px'
                        	});
                    });

                }

                $('.mega-channel .sw-channel-dropdown').each(function(index, dropdown){
                	if($(dropdown).parent().position().top > 40) {
                    	$(dropdown).css('top', '103px');
                    }
                });

            }

        },

