        "Search": function() {
            //--> SEARCH TEXT
            $('#gb-search-outer').prepend($('#sw-mystart-search script'), $('#sw-search-input'));
            $("#sw-search-input").removeAttr("placeholder title");
            $("#sw-search-input").attr("value", "Search...");
			$("#sw-search-input").focus(function() {
                if($(this).val() == "Search...") {
                    $(this).val("");
                }
            });
            $("#sw-search-input").blur(function() {
                if($(this).val() == "") {
                    $(this).val("Search...");
                }
			});




            //--> Click and
            $('#gb-search-btn').unbind().click(function(){

            	if($(this).hasClass('open')) {
                	$(this).removeClass('open').attr('aria-label', 'Open Search');
                	$('#sw-search-input').attr('aria-hidden', 'true').animate({
                    	'width' : '0px',
                        'padding' : '14px 0px 15px'
                        }, 750, function(){
                        	$('#sw-search-input').hide();
                        });
                } else {
                	$(this).addClass('open').attr('aria-label', 'Close Search');
                	$('#sw-search-input').attr('aria-hidden', 'false').show().animate({
                    	'width' : '215px',
                        'padding' : '14px 20px 15px'
                        }, 750, function(){
                        	$('#sw-search-input').focus();
                        });
                }
            });

        },

        "CalendarListView": function() {
            if($(".ui-widget.app.calendar").length) {
                // AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
                if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
                    $("a.ui-btn-toggle.list").click();
                    $("a.ui-btn-toggle.month").click();
                    $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
                }

                switch(this.GetBreakPoint()) {
                    case "desktop": case "768": case "640":
                        $(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
                    break;
                    case "480": case "320":
                        $(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
                        $(".ui-btn-toggle.month").removeClass("active");
                        $(".ui-btn-toggle.list").addClass("active");
                    break;
                }
            }
        },

        "KeyboardEvents": function() {
        	//ESCAPE BUTTON FUNCTIONALITY
            $('.gb-dropdown a').on('keydown', function(e) {
            	if(e.keyCode === 27) {
                	$(this).closest('.gb-dropdown').slideUp().closest('.gb-mystart-dropdown').find('button').focus();

                }

            });	
        },

        "UpdateAttributes":function() {
        	//GET HREF FROM PHONE NUMBER AND ADD TO ARIA LABEL
            var phoneNum = $('a.gb-header-phone').attr('href').slice(4);
            $('a.gb-header-phone').attr('aria-label', 'Phone number: ' + phoneNum);
            $('a.gb-header-phone span').attr('aria-hidden', true);
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }
    };

    function youTubeVideoDims() {

        // GET SCREEN DIMENSIONS
        var screenWidth = $(window).width();
        var screenHeight = $(window).height();
        var vidWidth = screenWidth, vidHeight = 0;

        // SET WIDTH, HEIGHT, AND RATIO
        var initW = 1920, initH = 1080;
        var ratio = initH / initW;
        vidHeight = vidWidth * ratio;
        if(vidHeight < screenHeight){
            vidHeight = screenHeight;
            vidWidth = vidHeight / ratio;
        }

        // CENTER VIDEO
        var centerWidth = (vidWidth - screenWidth) / 2;
        var centerHeight = (vidHeight - screenHeight) / 2;

        // APPLY THE DIMENSIONS
        $("#youtube-video").css({"width": vidWidth + "px", "height": vidHeight + "px", "margin": "-" + centerHeight + "px 0px 0px -" + centerWidth + "px"});

    }

    function onYouTubeIframeAPIReady() {

        var wW = $(window).width();

        // CREATE THE IFRAME AFTER API IS LOADED
        var player;
        player = new YT.Player("youtube-video", {
            height: $(window).height(),
            width: wW,
            videoId: "lvTTFmAHjEA",
            playerVars: { 
                "modestbranding": 1,
                "controls": 0, 
                "rel" : 0,
                "loop": 1,
                "autoplay": 1,
                "wmode": "opaque"
            },
            events: {
                // YOUTUBE PLAYER IS READY
                "onReady": function() {
                    // SET VIDEO DIMENSIONS
                    youTubeVideoDims();

                    // MUTE THE VIDEO
                    player.mute();

                    // MAKE SURE TO AUTO PLAY
                    player.playVideo();

                    // BIND VIDEO EVENTS
                    $('#video-btn').click(function(){
                        //PLAY
                        if($(this).hasClass('paused')){
                            player.playVideo();
                            $(this).removeClass("paused").attr('aria-label', 'Pause video.');
                            $('span', this).text('Pause Video');
                        } 
                        //PAUSE
                        else {
                            player.pauseVideo();
                            $(this).addClass("paused").attr('aria-label', 'Play video.');
                            $('span', this).text('Play Video');
                        }
                    });

                    //--> Add Video Title, Caption, Button
                    var addVideoDesc = 0;
                    var videoDesc = "";
                    if("Welcome to the 2020 School Year" != "") {
                        videoDesc += "
Welcome to the 2020 School Year";
                        addVideoDesc = 1;
					}

                    if("Click on (COVID-19) in our menu for updates." != "") {
                        videoDesc += "
Click on (COVID-19) in our menu for updates.";
                        addVideoDesc = 1;
                    }

                    if("" != "") {
                        videoDesc += "";
                        addVideoDesc = 1;
                    }
					if(addVideoDesc == 1) {
                        $('#video-desc').html(videoDesc);
                    }
                },
                "onStateChange": function(){
                    // VIDEO HAS ENDED

                },
                "onError": function(e){
                    console.log(e);
                }
            }
        });
        $('iframe#youtube-video').attr('tabindex', '-1');

    }

    $(document).ready(function(){

    	if($('#youtube-video').length) {
        	onYouTubeIframeAPIReady();
        }


    });

    $(window).resize(function(){

        if($('#gb-page.hp').length) {
            youTubeVideoDims();
        }

    });

