	function ajaxStylesInjector() {
		window.$ = jQuery
		$('.mk-dynamic-styles').each(function() {
			var $this = $(this),
				id = $this.attr('id'),
				styles = $this.html();

			if(dynamic_styles_ids.indexOf(id) === -1) {
				$('style[data-ajax]').append(styles);
			}

			dynamic_styles_ids.push(id);
		});
	};
