div.script:
$(document).ready(function () {
    $("#5e7c29ec-3fd3-4d45-b17e-69f70a330cab").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 3000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 10 > 1 ? autoPlayValue : false;
    }
});

$(document).ready(function () {
    $("#4b29c699-951d-4d72-b239-334185b3b41c").owlCarousel({
        navigation: true, // Show next and prev buttons
        navigationText: [
             "",
             ""
             ],
        items: '10',
        autoPlay : getAutoPlayValue(),

        mouseDrag: false,
        pagination:true,
        paginationNumbers: false,
        responsive:true,
        singleItem: true});

    $('div.promotional-carousel .owl-controls').on('click', function () {
        $('#4b29c699-951d-4d72-b239-334185b3b41c').trigger('owl.stop')
    })

    $('div.promotional-carousel').keyup(function (e) {
        if (e.keyCode == 9 || e.which == 9) {
            $('#4b29c699-951d-4d72-b239-334185b3b41c').trigger('owl.stop');
        }
    });

    // Prevent default on click of owl pagination
    $('.owl-page').on('click', '[href]', function(event){
        event.preventDefault();
    });

    function getAutoPlayValue() {
        var autoPlayValue = 5000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 8 > 1 ? autoPlayValue : false;
    }

});

jQuery(document).ready(function () {
    jQuery('#4a40c274-319d-4b59-9c92-a9fe0d3ee11b').hide();
    jQuery('#4a40c274-319d-4b59-9c92-a9fe0d3ee11b').listSolrResults({
        currentPagePath: '/content/schools/mglva/en',
        label: 'News',
        listFrom: 'solr',
        limit: '3',
        listType: 'news',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/news.html',
        viewAllTitle: 'View All',
        uuid: '4a40c274-319d-4b59-9c92-a9fe0d3ee11b',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

