$(document).ready(function() {

  if( $('.upcoming-events').length ){
      $('.upcoming-events a.cta').appendTo('#events > .inside-padding:last-child');

      if( window.location.href.indexOf('&action=design') >= 0 ){
        $('.upcoming-events').addClass('active');
      } else {
        var events = new Array ();
        $('.upcoming-events').each(function(){
          events.push($(this));
        });

        var calLinks = new Array ();
        $('#events > .inside-padding a.cta').each(function(){
          calLinks.push($(this));
        });

        $('.event-tabs a').first().addClass('active');
        $('.upcoming-events').first().addClass('active');
        $('#events > .inside-padding a.cta').first().addClass('active');

        $('.event-tabs a').click(function(e){
          e.preventDefault();
          var $tab = $(this);
          var tabPos = $(this).index();
          var eventPos = events[tabPos];
          var linkPos = calLinks[tabPos];

          if( $(this).hasClass('active') ){
          } else {
            $('.event-tabs a').removeClass('active');
            $tab.addClass('active');

            $('.upcoming-events').removeClass('active');
            eventPos.addClass('active');

            $('#events > .inside-padding a.cta').removeClass('active');
            linkPos.addClass('active');
          }
        });
      }
  }

});

