$(".group852265").colorbox({rel:'group852265', maxWidth: '80%'});
    $( window ).resize( function(){
        if( $('#cboxOverlay' ).css( 'visibility' ) ){
        $(".group852265").colorbox.resize({ innerWidth: '80%', scalePhotos: 'true'});
    }
});
var $gallery852265 = $("a[rel=group852265]").colorbox({maxWidth: "80%"});
$("a#openGallery852265").click(function(e){
    e.preventDefault();
    $gallery852265.eq(0).click();
});

