            $(document).on("click keypress", "#gb-additional-global-icons button", function(e) { 
                if(_this.A11yClick(e)) {
                    if($("#gb-page").hasClass("open") && $(this).hasClass("open")) {
                        $("#gb-page").removeClass("open");
                        $("#gb-additional-global-icons button").attr("aria-expanded", "false").removeClass("open");
                        $("#gb-icons-overlay-outer").attr("aria-hidden", "true").fadeOut(250, function() { $(".gb-icons-overlay-inner").attr("aria-hidden", "true").hide(); });
                    } else if($("#gb-page").hasClass("open")) {
                        $("#gb-additional-global-icons button").attr("aria-expanded", "false").removeClass("open");
                        $(".gb-icons-overlay-inner").attr("aria-hidden", "true").hide();
                        $(this).attr("aria-expanded", "true").addClass("open");
                        if($(".gb-icons-overlay-inner").eq($(this).index()).find(".global-icon").length > 6) {
                            $("#gb-icons-overlay-outer").attr("data-overlay-position", "2");
                        } else {
                            $("#gb-icons-overlay-outer").attr("data-overlay-position", "1");
                        }
                        $(".gb-icons-overlay-inner").eq($(this).index()).attr("aria-hidden", "false").show();
                        $(".gb-icons-overlay-inner").eq($(this).index()).find(".global-icon").first().focus();
                    } else {
                        $("#gb-page").addClass("open");
                        $(this).attr("aria-expanded", "true").addClass("open");
                        if($(".gb-icons-overlay-inner").eq($(this).index()).find(".global-icon").length > 6) {
                            $("#gb-icons-overlay-outer").attr("data-overlay-position", "2");
                        } else {
                            $("#gb-icons-overlay-outer").attr("data-overlay-position", "1");
                        }
                        $(".gb-icons-overlay-inner").eq($(this).index()).attr("aria-hidden", "false").show();
                        $("#gb-icons-overlay-outer").attr("aria-hidden", "false").fadeIn(250);
                        $(".gb-icons-overlay-inner").eq($(this).index()).find(".global-icon").first().focus();
                    }
                }
            });

            $(document).on("click keydown", ".gb-icons-overlay-close-btn button", function(e) { 
                e.preventDefault();
                if(_this.A11yClick(e)) {
                    $("#gb-page").removeClass("open");
                    $("#gb-additional-global-icons button").attr("aria-expanded", "false").removeClass("open");
                    $("#gb-icons-overlay-outer").attr("aria-hidden", "true").fadeOut(250);
                    $(".gb-icons-overlay-inner").attr("aria-hidden", "true").hide();
                    $("#gb-additional-global-icons button").eq($(this).parents(".gb-icons-overlay-inner").index()).focus();
                } else if(e.keyCode == _this.KeyCodes.tab) {
                	$(this).parents(".gb-icons-overlay-inner").find(".global-icon").first().focus();
                }
            });

            $(document).on("keydown", "#gb-icons-overlay .global-icon", function(e) {
                if(e.keyCode == _this.KeyCodes.tab && $(this).index() == 0) {
                    if(e.shiftKey) {
                        e.preventDefault();
                        $(this).parent().next().find("button").focus();
                    }
                }
            });

            $("#gb-icons-overlay-outer").on('focusout', function() {
                var $elem = $(this);
                setTimeout(function() {
                    var hasFocus = !! ($elem.find(':focus').length > 0);
                    if (! hasFocus) {
                        if($("#gb-page").removeClass("open") && $("#gb-additional-global-icons button.open").length > 0) {
                        	$("#gb-additional-global-icons button").attr("aria-expanded", "false").removeClass("open");
                        	$("#gb-icons-overlay-outer").attr("aria-hidden", "true").fadeOut(250, function() { $(".gb-icons-overlay-inner").attr("aria-hidden", "true").hide(); });
                        }
                    }
                }, 10);
            });
        },

