        "RsMenu": function() {
            var extraMenuOptions = "";
        	var extraMenuItems = "";    	

          	$('[data-rs-menu="true"]').each(function(index) { 
            	if(extraMenuOptions != "") {
                    extraMenuOptions += ";"+$(this).attr("data-rs-menu-text");
                } else {
                	extraMenuOptions += $(this).attr("data-rs-menu-text");
                }
                $("a", this).each(function() {
                    if(extraMenuItems != "") {
                        extraMenuItems += ";"+(index+1)+","+$(this).text()+","+$(this).attr("href")+","+$(this).attr("target");
                    } else {
                        extraMenuItems += (index+1)+","+$(this).text()+","+$(this).attr("href")+","+$(this).attr("target");
                    }     	
                });
            });

            $("body").csRsMenu({
                "breakPoint" : 768,
                "slideDirection" : "left-to-right",
                "primaryColor" : "#1A1A1A",
                "secondaryColor" : "#0C0C0C",
                "accentColor" : "#BABABA",
                "menuButtonParent" : "#gb-header-right",
                "showMenuBtnText" : "yes",
                "menuBtnText" : "MENU",
                "showSchools" : "yes",
                "showTranslate" : "yes",
                "translateId" : "none",
                "showAccount" : "yes",
                "usePageListNavigation" : "yes",
                "extraMenuOptions" : extraMenuOptions,
                "extraMenuItems" : extraMenuItems,
                "siteID" : "",
                "allLoaded" : function(){ }
            });
        },

