var style2083 = "";
    $(document).ready(function () {

        createTableView2083(2083, "False", "False");
        
        windowresize2083();
    });

	$(window).resize( function () {
      windowresize2083();
    });

	function isTable2083Responsive() {
    	var contentBreakPoint2083 = 600;
        
        if ('' != '') {
        	contentBreakPoint2083 = '';
            
        }
       
    	var screenWidth = (window.innerWidth > 0) ? window.innerWidth : screen.width;
		var contentWidth2083 = $("#tableApp2083").width();
        // Jason S - changed to trigger responsive for any screen size under 1024px 
        var deviceBool2083 = (screenWidth < 1024);
		
        if ( deviceBool2083 || contentWidth2083 < contentBreakPoint2083 ) {
        	return true;
        } else {
        	return false;
        }
    }
    
	function windowresize2083() {
		// trigger resposive if < 1024 screen size or content size below 600 - used for table app in multiple column layouts
        if ( isTable2083Responsive() ) {
            $("#tableApp2083").addClass("triggerResponsive");
            addResponsiveStyle2083();
        } else {
            $("#tableApp2083").removeClass("triggerResponsive");
            $("#tableapp-responsivestyle-tableApp2083").remove();
        } 
    }
    
    function addResponsiveStyle2083() {
        if ($("#tableapp-responsivestyle-tableApp2083").length == 0 && style2083 != "") {
    		// Jason S. - changed max width to 1024 to prevent losing headings when responsiveness is triggered SCB-7487
        	$('head').append("");
        }
    }

    function createTableView2083(id, hasHeaderRow, hasHeaderCol) {
		
        if ($("#hidData" + id).val() != "") {
            var tableId = "tableApp" + id
            var data = JSON.parse($("#hidData" + id).val());
            var classNames = JSON.parse($("#hidClassNames" + id).val());

            var headeHtml = "";
            var bodyHtml = "";
            var height = "auto";
            var width = "auto";
            var rowTitle = [];
            var columnTitle = [];
            style2083 = " caption {display: block;}.tableAppHead {position: absolute;top: -9999px;left: -9999px;}";
            $.each(data, function (row, rowItem) {
                if (hasHeaderRow == "True" && row == 0) {
                    headeHtml += "
";
                    $.each(rowItem, function (column, columnItem) {
                        var className = "";
                        if (classNames.length > row && classNames[row].length > column) {
                            className = classNames[row][column];
                        }
                        headeHtml += "
" + (columnItem == null || columnItem == "" ? " " : columnItem) + "";
                        rowTitle.push(columnItem == null ? " " : columnItem);
                    });
                    headeHtml += "";
                } else {
                    bodyHtml += "
";
                    $.each(rowItem, function (column, columnItem) {
                        var className = "";
                        if (classNames.length > row && classNames[row].length > column) {
                            className = classNames[row][column];
                        }
                        if (hasHeaderCol == "True" && column == 0) {
                            bodyHtml += "
" + (columnItem == null || columnItem == "" ? " " : columnItem) + "";
                            columnTitle.push(columnItem == null ? " " : columnItem);
                        } else {
                            bodyHtml += "
" + (columnItem == null || columnItem == "" ? " " : columnItem) + "";
                        }
                    });
                    bodyHtml += "";
                }
            });
            $("#" + tableId + " thead ").html(headeHtml);
            $("#" + tableId + " tbody ").html(bodyHtml);
            $.each(rowTitle, function (index, item) {
                if (hasHeaderCol == "True" && hasHeaderRow == "True") {
                    if (index != 0) {
                        style2083 += "#" + tableId + " td:nth-of-type(" + index + "):before { content: '" + $("
"+item+"").text() + "'; }";
                    }
                } else {

                    style2083 += "#" + tableId + " td:nth-of-type(" + (index + 1) + "):before { content: '" + $("
" + item + "").text() + "'; }";

                }


            });
            if (hasHeaderRow == "False" && hasHeaderCol == "True") {
                $.each(columnTitle, function (index, item) {
                    $("#" + tableId + " tbody tr").eq(index).prepend($("" + $("
" + item + "").text() + ""));
                });
            }
            
            if (isTable2083Responsive()) {
            	addResponsiveStyle2083();
            }
            
        }
    }

