div.script:
/* apply click, mouseover, mouseout events to the floatingCTA containers */
jQuery(document).ready(function() {
	jQuery('.floatingCTA .trigger').each(function() {
		jQuery(this).click(function(){ jQuery(this).parents('.floatingCTA').addClass('show') });
		jQuery(this).mouseover(function() { jQuery(this).parents('.floatingCTA').addClass('peek') });
		jQuery(this).mouseout(function() { jQuery(this).parents('.floatingCTA').removeClass('peek') });
	});
	jQuery('.floatingCTA .closeLink').each(function() {
		jQuery(this).click(function() { jQuery(this).parents('.floatingCTA').removeClass('show') });
	});
});

( function ( body ) {
	'use strict';
	body.className = body.className.replace( /\btribe-no-js\b/, 'tribe-js' );
} )( document.body );
