    var firstTab = true;

    document.addEventListener("userway:render_completed", function (event) {
        var userway = document.querySelector('div.userway');

        if (userway) {
            $('body').prepend('enable keyboard access to userway accessibility widget');

            $('div.userway').wrap('
');
            $('div.userway *').prop('tabindex', '-1');
        }
    });

    function enableTabToUserWay() {
        $('div.userway *').prop('tabindex', '0');
    }

    $('body').on('click', '#showUserWayWidget', function () {
        enableTabToUserWay();
    });

    $('body').on('keydown', '#showUserWayWidget', function (e) {
        if (e.keyCode === 13 || e.keyCode === 32) {
            enableTabToUserWay();
        }
    });

    $('body').on('keydown', function (e) {
        if (e.keyCode === 9) {
            if (firstTab) {
                e.preventDefault();
                firstTab = false;
                $('#showUserWayWidget').focus();
            }
        }
    });

function cycleFeature() {
	var index = $('.slideshowFeature .nav.nav-tabs > li.active').index();
	var length = $('.slideshowFeature .nav.nav-tabs > li').length;

	$('.slideshowFeature .nav.nav-tabs > li.active').removeClass('active');
	$('.slideshowFeature .tab-content > .tab-pane.active').removeClass('active');
	$('.slideshowFeature .nav.nav-tabs > li').eq((index + 1) % length).addClass('active');
	$('.slideshowFeature .tab-content > .tab-pane').eq((index + 1) % length).addClass('active');
}

